import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.for-each.js";
import TopBar from '../components/TopBar.vue';
import SideBar from '../components/SideBar.vue';
import ChartTypeSelector from '../components/chartIcons.vue';
import ChartDisplay from '../components/ChartDisplay.vue';
import ChartHistoryModal from '../components/ChartHistoryModal.vue';
import FileUploadModal from '../components/FileUploadModal.vue';
import DataPreviewModal from '../components/DataPreviewModal.vue';
import FileWorkspace from '../components/FileWorkspace.vue';
import FileStructureModal from '../components/FileStructureModal.vue';
import DataMappingPanel from '../components/DataMappingPanel.vue';
import { getFilePreview } from '../services/FileServices.js';
import { createTempDataset, convertToChartConfig, createMappingValidator } from '../utils/dragUtils.js';
import { ref, nextTick } from 'vue';

// 文件上传相关

export default {
  __name: 'ChartVisualizations',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const showFileUpload = ref(false);
    function UploadFiles() {
      showFileUpload.value = true;
    }

    // 数据预览相关
    const showDataPreview = ref(false);
    const currentDataFile = ref(null);
    const previewData = ref([]);

    // 工作区相关
    const workspaceFiles = ref([]);

    // 图表类型选择
    const selectedChartType = ref('');

    // 文件结构信息相关
    const showFileStructure = ref(false);
    const currentStructureFile = ref(null);
    const structurePreviewData = ref([]);

    // 图表配置
    const chartConfig = ref({
      renderMode: 'realtime',
      mappings: {},
      dataFilter: {
        mode: 'all',
        range: {
          start: 1,
          end: 100
        },
        conditions: []
      }
    });

    // 映射验证器
    const mappingValidator = createMappingValidator({});

    // 图表选项 - 初始为空，等待用户配置后生成
    const chartOption = ref(null);

    // 图表存储区（可从后端获取）
    const chartHistory = ref([{
      title: '销售柱状图',
      option: {
        title: {
          text: '销售柱状图'
        },
        tooltip: {},
        xAxis: {
          data: ['一季度', '二季度', '三季度', '四季度']
        },
        yAxis: {},
        series: [{
          type: 'bar',
          data: [120, 200, 150, 80]
        }]
      }
    }, {
      title: '折线趋势',
      option: {
        title: {
          text: '折线趋势'
        },
        tooltip: {},
        xAxis: {
          data: ['1月', '2月', '3月', '4月']
        },
        yAxis: {},
        series: [{
          type: 'line',
          data: [30, 50, 80, 65]
        }]
      }
    }, {
      title: '饼图示例',
      option: {
        title: {
          text: '饼图示例'
        },
        tooltip: {},
        series: [{
          type: 'pie',
          radius: '60%',
          data: [{
            value: 40,
            name: 'A类'
          }, {
            value: 32,
            name: 'B类'
          }, {
            value: 28,
            name: 'C类'
          }]
        }]
      }
    }]);
    const showHistory = ref(false);
    function openHistory() {
      showHistory.value = true;
    }
    function previewChart(item) {
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = JSON.parse(JSON.stringify(item.option));
        showHistory.value = false;
      });
    }
    function deleteChart(idx) {
      chartHistory.value.splice(idx, 1);
    }

    // 图表选择
    function onChartTypeSelect(type) {
      selectedChartType.value = type;
      console.log(`Selected chart type: ${type}`);

      // 根据图表类型更新配置
      updateChartConfig(type);
    }

    // 工作区相关方法
    function handleWorkspaceUpdate(files) {
      console.log('ChartVisualizations - handleWorkspaceUpdate called with files:', files);
      workspaceFiles.value = [...files];
      console.log('ChartVisualizations - Workspace updated:', workspaceFiles.value.length, 'files');

      // 检查每个文件的预览数据
      workspaceFiles.value.forEach(file => {
        console.log(`ChartVisualizations - File ${file.name}:`, {
          id: file.id,
          hasPreviewData: !!file.previewData,
          previewDataLength: file.previewData ? file.previewData.length : 0,
          headers: file.headers
        });
      });
    }
    function handleWorkspaceRemove(file) {
      console.log('File removed from workspace:', file.name);
      // 从工作区数组中移除文件
      const index = workspaceFiles.value.findIndex(f => f.id === file.id);
      if (index !== -1) {
        workspaceFiles.value.splice(index, 1);
      }
    }
    function handleWorkspacePreview(file) {
      // 预览工作区中的文件
      console.log('ChartVisualizations - Preview workspace file:', file.name);
      console.log('ChartVisualizations - File info:', file);
      currentDataFile.value = file;

      // 使用与 FileUploadModal 相同的预览逻辑
      if (file.previewData && file.previewData.length > 0) {
        // 如果文件已经有预览数据，直接使用
        console.log('ChartVisualizations - Using cached preview data');
        previewData.value = file.previewData;
        showDataPreview.value = true;
      } else {
        // 否则读取文件内容
        console.log('ChartVisualizations - Loading file preview from server');
        loadFilePreview(file);
      }
    }

    // 加载文件预览数据
    async function loadFilePreview(file) {
      try {
        console.log('ChartVisualizations - loadFilePreview called for:', file.name, 'with ID:', file.id);
        const previewResult = await getFilePreview(file.id);
        console.log('ChartVisualizations - getFilePreview result:', previewResult);
        previewData.value = previewResult.data;
        showDataPreview.value = true;
        console.log('ChartVisualizations - Preview data loaded successfully');
      } catch (error) {
        console.error('ChartVisualizations - Preview failed:', error);
        alert('Preview failed: ' + error.message);
      }
    }
    function handleWorkspaceClear() {
      console.log('Workspace cleared');
      workspaceFiles.value = [];
    }

    // 显示文件结构信息
    function handleShowFileStructure(file) {
      console.log('Show file structure:', file.name);
      currentStructureFile.value = file;

      // 获取文件的预览数据用于分析结构
      if (file.previewData) {
        structurePreviewData.value = file.previewData;
        showFileStructure.value = true;
      } else {
        loadFileStructureData(file);
      }
    }

    // 加载文件结构数据
    async function loadFileStructureData(file) {
      try {
        const previewResult = await getFilePreview(file.id);
        structurePreviewData.value = previewResult.data;
        showFileStructure.value = true;
      } catch (error) {
        console.error('加载文件结构失败:', error);
        alert('加载文件结构失败: ' + error.message);
      }
    }

    // 处理列拖拽
    function handleColumnDrag(event) {
      console.log('Column drag event:', event);
      // 这里可以添加拖拽反馈逻辑
    }

    // 更新图表配置
    function updateChartConfig(chartType) {
      chartConfig.value.chartType = chartType;

      // 根据不同图表类型清空或调整配置
      if (chartType !== selectedChartType.value) {
        chartConfig.value.mappings = {};
      }
    }

    // 处理图表配置变化
    function handleChartConfigChange(config) {
      console.log('Chart config changed:', config);
      Object.assign(chartConfig.value, config);
    }

    // 处理图表生成
    function handleGenerateChart(config) {
      console.log('ChartVisualizations - Generate chart with config:', config);
      console.log('Current workspace files:', workspaceFiles.value);
      try {
        // 验证必需的配置
        if (!config.chartType) {
          throw new Error('未选择图表类型');
        }
        if (!config.basicConfig || Object.keys(config.basicConfig).length === 0) {
          throw new Error('未配置基础映射');
        }

        // 验证工作区文件
        if (!workspaceFiles.value || workspaceFiles.value.length === 0) {
          throw new Error('工作区没有文件');
        }

        // 验证文件是否有预览数据
        const filesWithData = workspaceFiles.value.filter(file => file.previewData && file.previewData.length > 0);
        if (filesWithData.length === 0) {
          throw new Error('工作区文件没有有效的预览数据');
        }
        console.log('Files with preview data:', filesWithData.length);

        // 创建临时数据集
        const tempData = createTempDataset(workspaceFiles.value, config.basicConfig, config.dataFilter);
        console.log('Temp data created:', tempData.length, 'rows');
        console.log('Temp data sample:', tempData.slice(0, 3));

        // 转换为图表配置
        const echartConfig = convertToChartConfig(config, tempData, config.chartType);
        console.log('EChart config generated:', echartConfig);

        // 更新图表显示
        chartOption.value = null;
        nextTick(() => {
          chartOption.value = echartConfig;
          console.log('Chart option updated:', chartOption.value);
        });
        console.log('Chart generated successfully');
      } catch (error) {
        console.error('图表生成失败:', error);
        alert('图表生成失败: ' + error.message);
      }
    }
    const __returned__ = {
      showFileUpload,
      UploadFiles,
      showDataPreview,
      currentDataFile,
      previewData,
      workspaceFiles,
      selectedChartType,
      showFileStructure,
      currentStructureFile,
      structurePreviewData,
      chartConfig,
      mappingValidator,
      chartOption,
      chartHistory,
      showHistory,
      openHistory,
      previewChart,
      deleteChart,
      onChartTypeSelect,
      handleWorkspaceUpdate,
      handleWorkspaceRemove,
      handleWorkspacePreview,
      loadFilePreview,
      handleWorkspaceClear,
      handleShowFileStructure,
      loadFileStructureData,
      handleColumnDrag,
      updateChartConfig,
      handleChartConfigChange,
      handleGenerateChart,
      TopBar,
      SideBar,
      ChartTypeSelector,
      ChartDisplay,
      ChartHistoryModal,
      FileUploadModal,
      DataPreviewModal,
      FileWorkspace,
      FileStructureModal,
      DataMappingPanel,
      get getFilePreview() {
        return getFilePreview;
      },
      get createTempDataset() {
        return createTempDataset;
      },
      get convertToChartConfig() {
        return convertToChartConfig;
      },
      get createMappingValidator() {
        return createMappingValidator;
      },
      ref,
      nextTick
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
