/**
 * 图表配置管理器
 * 提供配置管理、验证、序列化等高级功能
 */

import { getChartConfig, validateChartConfiguration } from './chartConfigs.js'
import { inferDataType } from './dataTypes.js'

export class ChartConfigManager {
  constructor() {
    this.configs = new Map()
    this.history = []
    this.currentVersion = 0
  }

  // 创建新配置
  createConfig(chartType, id = null) {
    const configId = id || this.generateId()
    const chartConfig = getChartConfig(chartType)

    const config = {
      id: configId,
      chartType: chartType,
      fieldMappings: {},
      options: chartConfig.getDefaultConfig(),
      version: 1,
      created: new Date().toISOString(),
      modified: new Date().toISOString()
    }

    this.configs.set(configId, config)
    this.addToHistory('create', configId, null, config)

    return config
  }

  // 获取配置
  getConfig(id) {
    return this.configs.get(id)
  }

  // 更新配置
  updateConfig(id, updates) {
    const config = this.configs.get(id)
    if (!config) {
      throw new Error(`Configuration with id ${id} not found`)
    }

    const oldConfig = { ...config }
    const newConfig = {
      ...config,
      ...updates,
      version: config.version + 1,
      modified: new Date().toISOString()
    }

    this.configs.set(id, newConfig)
    this.addToHistory('update', id, oldConfig, newConfig)

    return newConfig
  }

  // 删除配置
  deleteConfig(id) {
    const config = this.configs.get(id)
    if (!config) {
      throw new Error(`Configuration with id ${id} not found`)
    }

    this.configs.delete(id)
    this.addToHistory('delete', id, config, null)

    return true
  }

  // 复制配置
  cloneConfig(id, newId = null) {
    const config = this.configs.get(id)
    if (!config) {
      throw new Error(`Configuration with id ${id} not found`)
    }

    const clonedConfig = {
      ...config,
      id: newId || this.generateId(),
      version: 1,
      created: new Date().toISOString(),
      modified: new Date().toISOString()
    }

    this.configs.set(clonedConfig.id, clonedConfig)
    this.addToHistory('clone', clonedConfig.id, null, clonedConfig)

    return clonedConfig
  }

  // 验证配置
  validateConfig(id) {
    const config = this.configs.get(id)
    if (!config) {
      throw new Error(`Configuration with id ${id} not found`)
    }

    return validateChartConfiguration(config.chartType, config.fieldMappings, config.options)
  }

  // 自动字段映射
  autoMapFields(id, columns) {
    const config = this.configs.get(id)
    if (!config) {
      throw new Error(`Configuration with id ${id} not found`)
    }

    const chartConfig = getChartConfig(config.chartType)
    const fieldMapping = chartConfig.getFieldMapping()

    // 推断列的数据类型
    const processedColumns = columns.map(col => ({
      ...col,
      dataType: inferDataType(col.values || [])
    }))

    // 获取字段建议
    const suggestions = chartConfig.getFieldSuggestions(processedColumns)

    // 自动映射字段
    const autoMappings = {}

    Object.entries(suggestions).forEach(([fieldName, suggestion]) => {
      if (suggestion.suggestions.length > 0) {
        const bestSuggestion = suggestion.suggestions[0]
        if (bestSuggestion.confidence > 0.7) {
          autoMappings[fieldName] = {
            field: bestSuggestion.column.name,
            type: bestSuggestion.column.dataType
          }
        }
      }
    })

    // 更新配置
    this.updateConfig(id, {
      fieldMappings: {
        ...config.fieldMappings,
        ...autoMappings
      }
    })

    return autoMappings
  }

  // 导出配置
  exportConfig(id) {
    const config = this.configs.get(id)
    if (!config) {
      throw new Error(`Configuration with id ${id} not found`)
    }

    return {
      ...config,
      exportedAt: new Date().toISOString(),
      version: config.version
    }
  }

  // 导入配置
  importConfig(configData) {
    const config = {
      ...configData,
      id: configData.id || this.generateId(),
      version: 1,
      created: new Date().toISOString(),
      modified: new Date().toISOString()
    }

    delete config.exportedAt

    this.configs.set(config.id, config)
    this.addToHistory('import', config.id, null, config)

    return config
  }

  // 批量操作
  batchUpdate(updates) {
    const results = []

    updates.forEach(update => {
      try {
        const result = this.updateConfig(update.id, update.data)
        results.push({ success: true, id: update.id, config: result })
      } catch (error) {
        results.push({ success: false, id: update.id, error: error.message })
      }
    })

    return results
  }

  // 搜索配置
  searchConfigs(query) {
    const results = []

    this.configs.forEach((config, id) => {
      const searchText = `${config.chartType} ${JSON.stringify(config.options)}`.toLowerCase()
      if (searchText.includes(query.toLowerCase())) {
        results.push(config)
      }
    })

    return results
  }

  // 获取所有配置
  getAllConfigs() {
    return Array.from(this.configs.values())
  }

  // 获取配置统计
  getStats() {
    const stats = {
      total: this.configs.size,
      byType: {},
      byDate: {},
      averageComplexity: 0
    }

    this.configs.forEach(config => {
      // 按类型统计
      stats.byType[config.chartType] = (stats.byType[config.chartType] || 0) + 1

      // 按日期统计
      const date = config.created.split('T')[0]
      stats.byDate[date] = (stats.byDate[date] || 0) + 1

      // 复杂度统计
      const complexity = this.calculateComplexity(config)
      stats.averageComplexity += complexity
    })

    stats.averageComplexity /= this.configs.size || 1

    return stats
  }

  // 计算配置复杂度
  calculateComplexity(config) {
    let complexity = 0

    // 字段映射复杂度
    complexity += Object.keys(config.fieldMappings).length * 10

    // 配置选项复杂度
    complexity += Object.keys(config.options).length * 5

    // 特殊配置复杂度
    if (config.options.showDataLabels) complexity += 5
    if (config.options.showTrendline) complexity += 10
    if (config.options.stack) complexity += 15

    return complexity
  }

  // 获取历史记录
  getHistory(limit = 50) {
    return this.history.slice(-limit)
  }

  // 添加历史记录
  addToHistory(action, id, oldValue, newValue) {
    this.history.push({
      version: ++this.currentVersion,
      action,
      id,
      oldValue,
      newValue,
      timestamp: new Date().toISOString()
    })

    // 限制历史记录数量
    if (this.history.length > 1000) {
      this.history = this.history.slice(-500)
    }
  }

  // 撤销操作
  undo() {
    if (this.history.length === 0) {
      return false
    }

    const lastAction = this.history[this.history.length - 1]

    switch (lastAction.action) {
      case 'create':
        this.configs.delete(lastAction.id)
        break
      case 'update':
        this.configs.set(lastAction.id, lastAction.oldValue)
        break
      case 'delete':
        this.configs.set(lastAction.id, lastAction.oldValue)
        break
      case 'clone':
        this.configs.delete(lastAction.id)
        break
    }

    this.history.pop()
    return true
  }

  // 生成唯一ID
  generateId() {
    return 'chart_config_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9)
  }

  // 清空所有配置
  clear() {
    this.configs.clear()
    this.history = []
    this.currentVersion = 0
  }

  // 序列化为JSON
  toJSON() {
    return {
      configs: Array.from(this.configs.entries()),
      history: this.history,
      currentVersion: this.currentVersion
    }
  }

  // 从JSON反序列化
  fromJSON(data) {
    this.configs = new Map(data.configs)
    this.history = data.history || []
    this.currentVersion = data.currentVersion || 0
  }
}

// 创建全局配置管理器实例
export const globalConfigManager = new ChartConfigManager()

// 便利函数
export function createChartConfig(chartType, id = null) {
  return globalConfigManager.createConfig(chartType, id)
}

export function getChartConfiguration(id) {
  return globalConfigManager.getConfig(id)
}

export function updateChartConfiguration(id, updates) {
  return globalConfigManager.updateConfig(id, updates)
}

export function validateChartConfig(id) {
  return globalConfigManager.validateConfig(id)
}

export function autoMapFieldsForChart(id, columns) {
  return globalConfigManager.autoMapFields(id, columns)
}
