// fileUtils.js
// 公共文件相关工具函数

/* eslint-disable */

/**
 * 格式化文件大小
 * @param {number} bytes
 * @returns {string}
 */
export function formatFileSize(bytes) {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i]
}

/**
 * 获取文件状态文本
 * @param {string} status
 * @returns {string}
 */
export function getStatusText(status) {
    const statusMap = {
        'local': 'Local Cache',
        'error': 'Error'
    }
    return statusMap[status] || 'Local Cache'
}

/**
 * 获取文件状态图标名
 * @param {string} status
 * @returns {string}
 */
export function getStatusIconName(status) {
    const iconMap = {
        'local': 'localCache',
        'uploading': 'uploading',
        'uploaded': 'uploaded',
        'operating': 'sync',
        'error': 'error'
    }
    return iconMap[status] || 'unknown'
}
