/* eslint-disable */

// 主题河流图配置生成器
export default function themeRiverOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    return {
        tooltip: {
            trigger: 'axis',
        },
        legend: {
            data: Array.isArray(yDataArr) ? yDataArr.map(item => item.name) : []
        },
        series: {
            type: 'themeRiver',
            data: seriesData
        }
    };
}
