/* eslint-disable */

// 仪表盘配置生成器
export default function gaugeOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 支持高级 gaugeData 格式（如每项自定义 title/detail）
    let gaugeData = Array.isArray(seriesData)
        ? seriesData
        : (seriesData.name && seriesData.value)
            ? seriesData.name.map((n, i) => ({
                name: n,
                value: seriesData.value[i],
                title: seriesData.title && seriesData.title[i] ? seriesData.title[i] : undefined,
                detail: seriesData.detail && seriesData.detail[i] ? seriesData.detail[i] : undefined
            }))
            : [];

    return {
        tooltip: {
            formatter: '{a} <br/>{b} : {c}%'
        },
        series: [
            {
                type: 'gauge',
                anchor: {
                    show: true,
                    showAbove: true,
                    size: 18,
                    itemStyle: {
                        color: '#FAC858'
                    }
                },
                pointer: {
                    icon: 'path://M2.9,0.7L2.9,0.7c1.4,0,2.6,1.2,2.6,2.6v115c0,1.4-1.2,2.6-2.6,2.6l0,0c-1.4,0-2.6-1.2-2.6-2.6V3.3C0.3,1.9,1.4,0.7,2.9,0.7z',
                    width: 8,
                    length: '80%',
                    offsetCenter: [0, '8%']
                },
                progress: {
                    show: true,
                    overlap: true,
                    roundCap: true
                },
                axisLine: {
                    roundCap: true
                },
                data: gaugeData,
                title: {
                    fontSize: 14
                },
                detail: {
                    width: 40,
                    height: 14,
                    fontSize: 14,
                    color: '#fff',
                    backgroundColor: 'inherit',
                    borderRadius: 3,
                    formatter: '{value}%'
                }
            }
        ]
    };
};
