import { ref, watch, onMounted, onBeforeUnmount, computed } from 'vue';
import * as echarts from 'echarts';
import { chartIcons } from '@/assets/JS/SVG/chartIcons.js';
// 选用Bar图标作为空状态图标（更通用且有仪表感）

export default {
  __name: 'ChartDisplay',
  props: {
    option: {
      type: Object,
      required: true
    }
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */

    const emptyChartIcon = chartIcons.Bar || `<svg viewBox="0 0 24 24" width="48" height="48" fill="none" stroke="currentColor" stroke-width="2"><rect x="4" y="10" width="2" height="10" fill="currentColor"/><rect x="9" y="6" width="2" height="14" fill="currentColor"/><rect x="14" y="2" width="2" height="18" fill="currentColor"/><rect x="19" y="8" width="2" height="12" fill="currentColor"/></svg>`;
    const props = __props;
    const chartRef = ref(null);
    let chartInstance = null;
    const hasSeries = computed(() => {
      return props.option && Array.isArray(props.option.series) && props.option.series.length > 0;
    });
    function renderChart() {
      if (!hasSeries.value) return;
      if (!chartInstance && chartRef.value) {
        chartInstance = echarts.init(chartRef.value);
      }
      if (chartInstance && props.option) {
        chartInstance.setOption(props.option, true);
      }
    }
    watch(() => props.option, renderChart, {
      deep: true
    });
    onMounted(renderChart);
    onBeforeUnmount(() => {
      if (chartInstance) chartInstance.dispose();
    });
    const __returned__ = {
      emptyChartIcon,
      props,
      chartRef,
      get chartInstance() {
        return chartInstance;
      },
      set chartInstance(v) {
        chartInstance = v;
      },
      hasSeries,
      renderChart,
      ref,
      watch,
      onMounted,
      onBeforeUnmount,
      computed,
      get echarts() {
        return echarts;
      },
      get chartIcons() {
        return chartIcons;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
