import ChartThumbnail from './ChartThumbnail.vue';
import { ref, onMounted, onBeforeUnmount } from 'vue';
import LineChartIcon from '../svg/LineChartIcon.vue';
import BarChartIcon from '../svg/BarChartIcon.vue';
import PieChartIcon from '../svg/PieChartIcon.vue';
import ScatterChartIcon from '../svg/ScatterChartIcon.vue';
import MapChartIcon from '../svg/MapChartIcon.vue';
import KlineChartIcon from '../svg/KlineChartIcon.vue';
import RadarChartIcon from '../svg/RadarChartIcon.vue';
import BoxplotChartIcon from '../svg/BoxplotChartIcon.vue';
import HeatmapChartIcon from '../svg/HeatmapChartIcon.vue';
import GraphChartIcon from '../svg/GraphChartIcon.vue';
import TreeChartIcon from '../svg/TreeChartIcon.vue';
import TreemapChartIcon from '../svg/TreemapChartIcon.vue';
import SunburstChartIcon from '../svg/SunburstChartIcon.vue';
import ParallelChartIcon from '../svg/ParallelChartIcon.vue';
import SankeyChartIcon from '../svg/SankeyChartIcon.vue';
import FunnelChartIcon from '../svg/FunnelChartIcon.vue';
import GaugeChartIcon from '../svg/GaugeChartIcon.vue';
import PictorialBarChartIcon from '../svg/PictorialBarChartIcon.vue';
import ThemeRiverChartIcon from '../svg/ThemeRiverChartIcon.vue';
import CalendarChartIcon from '../svg/CalendarChartIcon.vue';
import AnimateIcon from '../Common/AnimateIcon.vue';

// 空状态图标轮播逻辑

export default {
  __name: 'ChartHistoryModal',
  props: {
    show: Boolean,
    chartHistory: Array
  },
  emits: ['close', 'preview', 'delete'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();

    /* eslint-disable */
    const iconList = [BarChartIcon, LineChartIcon, PieChartIcon, ScatterChartIcon, MapChartIcon, KlineChartIcon, RadarChartIcon, BoxplotChartIcon, HeatmapChartIcon, GraphChartIcon, TreeChartIcon, TreemapChartIcon, SunburstChartIcon, ParallelChartIcon, SankeyChartIcon, FunnelChartIcon, GaugeChartIcon, PictorialBarChartIcon, ThemeRiverChartIcon, CalendarChartIcon];
    const iconIndex = ref(0);
    const iconOpacity = ref(1);
    let iconTimer = null;
    let fadeTimer = null;
    function startIconLoop() {
      clearInterval(iconTimer);
      clearTimeout(fadeTimer);
      iconOpacity.value = 1;
      iconTimer = setInterval(() => {
        // 先淡出
        iconOpacity.value = 0;
        fadeTimer = setTimeout(() => {
          iconIndex.value = (iconIndex.value + 1) % iconList.length;
          iconOpacity.value = 1;
        }, 400);
      }, 3500);
    }

    // 拖拽浮窗相关
    const modalStyle = ref({
      left: '50vw',
      top: '12vh'
    });
    const dragOffset = {
      x: 0,
      y: 0
    };
    let dragging = false;
    function startDrag(e) {
      if (e.button !== 0) return;
      dragging = true;
      const modal = document.querySelector('.history-modal-draggable');
      const rect = modal.getBoundingClientRect();
      dragOffset.x = e.clientX - rect.left;
      dragOffset.y = e.clientY - rect.top;
      document.addEventListener('mousemove', onDrag);
      document.addEventListener('mouseup', stopDrag);
    }
    function onDrag(e) {
      if (!dragging) return;
      let left = e.clientX - dragOffset.x;
      let top = e.clientY - dragOffset.y;
      // 限制在窗口内
      const minLeft = 20;
      const minTop = 20;
      const maxLeft = window.innerWidth - 480 - 20;
      const maxTop = window.innerHeight - 420 - 20;
      left = Math.max(minLeft, Math.min(left, maxLeft));
      top = Math.max(minTop, Math.min(top, maxTop));
      modalStyle.value = {
        left: left + 'px',
        top: top + 'px'
      };
    }
    function stopDrag() {
      dragging = false;
      document.removeEventListener('mousemove', onDrag);
      document.removeEventListener('mouseup', stopDrag);
    }
    onMounted(() => {
      // 初始居中
      const w = 480;
      const h = 420;
      modalStyle.value = {
        left: (window.innerWidth - w) / 2 + 'px',
        top: (window.innerHeight - h) / 3 + 'px'
      };
    });
    onBeforeUnmount(() => {
      stopDrag();
    });
    // 删除确认
    const showDeleteConfirm = ref(false);
    const deleteIndex = ref(null);
    function confirmDelete(idx) {
      deleteIndex.value = idx;
      showDeleteConfirm.value = true;
    }
    function cancelDelete() {
      showDeleteConfirm.value = false;
      deleteIndex.value = null;
    }
    function handleDelete() {
      if (deleteIndex.value !== null) {
        emit('delete', deleteIndex.value);
        showDeleteConfirm.value = false;
        deleteIndex.value = null;
      }
    }

    // props

    const emit = __emit;
    function onClose() {
      emit('close');
    }
    function onPreview(item) {
      emit('preview', item);
    }
    const __returned__ = {
      iconList,
      iconIndex,
      iconOpacity,
      get iconTimer() {
        return iconTimer;
      },
      set iconTimer(v) {
        iconTimer = v;
      },
      get fadeTimer() {
        return fadeTimer;
      },
      set fadeTimer(v) {
        fadeTimer = v;
      },
      startIconLoop,
      modalStyle,
      dragOffset,
      get dragging() {
        return dragging;
      },
      set dragging(v) {
        dragging = v;
      },
      startDrag,
      onDrag,
      stopDrag,
      showDeleteConfirm,
      deleteIndex,
      confirmDelete,
      cancelDelete,
      handleDelete,
      emit,
      onClose,
      onPreview,
      ChartThumbnail,
      ref,
      onMounted,
      onBeforeUnmount,
      LineChartIcon,
      BarChartIcon,
      PieChartIcon,
      ScatterChartIcon,
      MapChartIcon,
      KlineChartIcon,
      RadarChartIcon,
      BoxplotChartIcon,
      HeatmapChartIcon,
      GraphChartIcon,
      TreeChartIcon,
      TreemapChartIcon,
      SunburstChartIcon,
      ParallelChartIcon,
      SankeyChartIcon,
      FunnelChartIcon,
      GaugeChartIcon,
      PictorialBarChartIcon,
      ThemeRiverChartIcon,
      CalendarChartIcon,
      AnimateIcon
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
