// 平行坐标系图配置生成器
import { getVisualMapInRangeColor } from "../utils/themeDispatcher";
// 平行坐标系图配置生成器
export default function parallelOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 解包数据
    const { dimensions, name } = seriesData;

    if (!dimensions || !name || !Array.isArray(dimensions) || !Array.isArray(name)) {
        return {};
    }


    // 1. 获取所有维度名（不包含 Category）
    // 假设 name[0] 里所有 key，去掉 'Category' 或其它分组字段
    const allKeys = Object.keys(name[0]);
    const groupKey = 'Category'; // 可根据实际情况调整
    const dimensionNames = allKeys.filter(k => k !== groupKey);

    // 2. 构造 parallelAxis（只用 dimensions 字段）
    const parallelAxis = dimensionNames.map((dim, idx) => {
        // 判断类型
        const isNumber = typeof name[0][dim] === 'number';
        return {
            dim: idx,
            name: dim,
            type: isNumber ? 'value' : 'category'
        };
    });

    // 3. 按 Category 分组，生成 series
    // 先将所有数据合并为一个数组
    const allData = Array.isArray(name) ? name : [];
    // 分组
    const groupMap = {};
    allData.forEach(item => {
        const key = item[groupKey] || '未分类';
        if (!groupMap[key]) groupMap[key] = [];
        groupMap[key].push(item);
    });

    const series = Object.entries(groupMap).map(([cat, arr]) => ({
        name: cat,
        type: 'parallel',
        lineStyle: {
            width: 2
        },
        data: arr.map(item => dimensionNames.map(dim => item[dim]))
    }));

    // 4. 组装 visualMap
    // 取第一个维度的数值范围作为 visualMap 映射（可根据实际需求调整）
    const firstDim = dimensionNames[0];
    const allValues = allData.map(item => item[firstDim]).filter(v => typeof v === 'number');
    const min = allValues.length ? Math.min(...allValues) : 0;
    const max = allValues.length ? Math.max(...allValues) : 100;
    const visualColors = getVisualMapInRangeColor(config.colorScheme);

    const visualMap = {
        show: true,
        type: 'continuous',
        inRange: {
            color: visualColors
        },
        min,
        max,
        dimension: 0, // 默认映射第一个维度
        orient: 'vertical',
        left: 'left',
        top: config.legendPosition || 'bottom',
    };

    const option = {
        parallelAxis,
        parallel: {
            left: '5%',
            right: '13%',
            bottom: '10%',
            parallelAxisDefault: {
                type: 'value',
                nameLocation: 'end',
                nameGap: 20
            }
        },
        series,
        visualMap: config.legendVisible === false ? undefined : visualMap,
        ...customOption
    };

    return option;
}
