/* eslint-disable */

// 桑基图配置生成器

export default function sankeyOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 解包
    const { data, links } = seriesData;

    return {
        title: {
            text: config.title || 'Chart of Sankey',
            subtext: config.subtext || '',
            left: 'center',
            top: 'top',
        },
        series: [{
            type: 'sankey',
            emphasis: {
                focus: 'adjacency'
            },
            data: data,
            links: links,
        }],
        ...customOption
    }
}
