/* eslint-disable */

// 关系图生成器
export default function graphOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const { nodes, edges } = seriesData;
    return {
        tooltip: {
            show: true,
            formatter: function (params) {
                if (params.dataType === 'node') {
                    return params.data.name || params.data.id;
                } else if (params.dataType === 'edge') {
                    return `${params.data.source} → ${params.data.target}` + (params.data.weight ? ` (权重: ${params.data.weight})` : '');
                }
                return '';
            }
        },
        toolbox: {
            show: true,
            feature: {
                dataZoom: { show: true },
                saveAsImage: { show: true },
                restore: { show: true },
                dataView: { show: true, readOnly: false },
                magicType: {
                    show: true,
                    type: ['force', 'chord', 'none']
                }
            }
        },
        legend: {
            data: Array.from(new Set(nodes.map(node => node.category))).filter(Boolean),
            top: config.legendPosition || 'bottom',
            show: config.legendVisible !== false,
        },
        series: [
            {
                type: 'graph',
                // 默认力引导布局
                layout: 'force',
                roam: true,
                data: nodes,
                links: edges,
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{b}'
                },
                force: {
                    repulsion: 200,
                    edgeLength: 100
                },
                lineStyle: {
                    color: 'source',
                    curveness: 0.3,
                },
                edgeLabel: {
                    show: false
                },
            }
        ],
        ...customOption,
    };
}
