/* eslint-disable */

// 关系图生成器
export default function graphOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const { nodes, edges } = seriesData;
    return {
        tooltip: {
            show: true,
            formatter: function (params) {
                if (params.dataType === 'node') {
                    return params.data.name || params.data.id;
                } else if (params.dataType === 'edge') {
                    return `${params.data.source} → ${params.data.target}` + (params.data.weight ? ` (权重: ${params.data.weight})` : '');
                }
                return '';
            }
        },
        legend: {
            show: false
        },
        series: [
            {
                type: 'graph',
                layout: 'force',
                roam: true,
                data: nodes,
                edges: edges,
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{b}'
                },
                force: {
                    repulsion: 200,
                    edgeLength: 100
                },
                lineStyle: {
                    color: '#aaa',
                    width: 2
                },
                edgeLabel: {
                    show: false
                },
                itemStyle: {
                    borderColor: '#333',
                    borderWidth: 1
                }
            }
        ],
        ...customOption,
        ...(customOption.series && Array.isArray(customOption.series)
            ? { series: customOption.series.map((ser, idx) => Object.assign({}, {
                type: 'graph',
                layout: 'force',
                roam: true,
                data: nodes,
                edges: edges,
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{b}'
                },
                force: {
                    repulsion: 200,
                    edgeLength: 100
                },
                lineStyle: {
                    color: '#aaa',
                    width: 2
                },
                edgeLabel: {
                    show: false
                },
                itemStyle: {
                    borderColor: '#333',
                    borderWidth: 1
                }
            }, ser)) } : {})
    };
}
