/* eslint-disable */

// 箱线图生成器
export default function boxplotOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const { seriesList, seriesData_boxplot } = seriesData;

    return {
        title: {
            text: 'Boxplot Chart',
            left: 'center',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold',
            }
        },
        legend: {
            type: 'scroll',
            show: config.legendVisible !== false,
            top: config.legendPosition || 'bottom',
            data: seriesList
        },
        tooltip: {
            trigger: 'item',
            axisPointer: {
                type: 'shadow'
            }
        },
        xAxis: {
            type: 'category',
            data: xData,
            axisLabel: {
                interval: 0,
                rotate: xData.length > 10 ? 45 : 0
            },
        },
        yAxis: {
            type: 'value',
            name: 'Value',
            splitArea: {
                show: true
            }
        },
        series: seriesList.map((ser, idx) => ({
            name: ser,
            type: 'boxplot',
            data: seriesData_boxplot[idx],
            tooltip: {
                formatter: function (param) {
                    return [
                        `${ser} - ${xData[param.dataIndex]}`,
                        `Min: ${param.data[0]}`,
                        `Q1: ${param.data[1]}`,
                        `Median: ${param.data[2]}`,
                        `Q3: ${param.data[3]}`,
                        `Max: ${param.data[4]}`
                    ].join('<br/>');
                }
            }
        })),
        ...customOption
    }
}
