import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import TopBar from '../components/TopBar.vue';
import SideBar from '../components/SideBar.vue';
import ChartTypeSelector from '../components/ChartTypeSelector.vue';
import DataMappingConfig from '../components/DataMappingConfig.vue';
import ChartDisplay from '../components/ChartDisplay.vue';
import ChartHistoryModal from '../components/ChartHistoryModal.vue';
import FileUploadModal from '../components/FileUploadModal.vue';
import DataPreviewModal from '../components/DataPreviewModal.vue';
import FileWorkspace from '../components/FileWorkspace.vue';
import FileStructurePanel from '../components/FileStructurePanel.vue';
import { getFilePreview } from '../services/FileServices.js';
import { ref, nextTick } from 'vue';

// 图表类型选择相关

export default {
  __name: 'ChartVisualizations',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const selectedChartType = ref('');

    // 图表配置和渲染相关
    const chartOption = ref(null);

    // 文件上传相关
    const showFileUpload = ref(false);
    function UploadFiles() {
      showFileUpload.value = true;
    }

    // 数据预览相关
    const showDataPreview = ref(false);
    const currentDataFile = ref(null);
    const previewData = ref([]);

    // 文件结构面板
    const showFileStructure = ref(false);

    // 工作区相关
    const workspaceFiles = ref([]);

    // 图表历史相关
    const showHistory = ref(false);
    const chartHistory = ref([]);
    function DataPreview() {
      if (workspaceFiles.value.length > 0) {
        currentDataFile.value = workspaceFiles.value[0];
        // 模拟预览数据
        previewData.value = generateMockPreviewData();
        showDataPreview.value = true;
      } else {
        alert('请先上传数据文件');
      }
    }
    function openHistory() {
      showHistory.value = true;
    }

    // 图表类型选择处理
    function onChartTypeSelect(chartType) {
      selectedChartType.value = chartType;
      console.log('选择图表类型:', chartType);
    }

    // 文件工作区处理
    function handleWorkspaceUpdate(files) {
      workspaceFiles.value = [...files];
    }
    function handleWorkspaceRemove(file) {
      const index = workspaceFiles.value.findIndex(f => f.id === file.id);
      if (index !== -1) {
        workspaceFiles.value.splice(index, 1);
      }
    }
    function handleWorkspacePreview(file) {
      currentDataFile.value = file;
      previewData.value = generateMockPreviewData();
      showDataPreview.value = true;
    }
    function handleWorkspaceClear() {
      workspaceFiles.value = [];
    }
    function handleShowFileStructure(file) {
      showFileStructure.value = true;
    }

    // 列拖拽处理
    function handleColumnDrag(dragData) {
      console.log('列拖拽:', dragData);
    }

    // 图表生成和预览
    function handleGenerateChart(config) {
      console.log('生成图表配置:', config);

      // 根据配置生成图表选项
      const option = generateChartOption(config);
      chartOption.value = option;

      // 保存到历史记录
      saveToHistory(config, option);
    }
    function handlePreviewChart(config) {
      console.log('预览图表配置:', config);

      // 生成预览图表选项
      const option = generateChartOption(config, true);
      chartOption.value = option;
    }

    // 生成图表选项
    function generateChartOption(config, isPreview = false) {
      const {
        chartType,
        configs,
        options,
        filter
      } = config;

      // 模拟生成 ECharts 配置
      const baseOption = {
        title: {
          text: options.title || `${chartType} 图表${isPreview ? ' (预览)' : ''}`,
          left: 'center',
          textStyle: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        tooltip: {
          trigger: 'item'
        },
        legend: {
          bottom: '10px',
          textStyle: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        }
      };

      // 根据图表类型生成特定配置
      switch (chartType) {
        case 'Line':
          return generateLineChart(baseOption, configs, options, isPreview);
        case 'Bar':
          return generateBarChart(baseOption, configs, options, isPreview);
        case 'Pie':
          return generatePieChart(baseOption, configs, options, isPreview);
        case 'Scatter':
          return generateScatterChart(baseOption, configs, options, isPreview);
        case 'Heatmap':
          return generateHeatmapChart(baseOption, configs, options, isPreview);
        default:
          return generateDefaultChart(baseOption, chartType, isPreview);
      }
    }

    // 生成线性图
    function generateLineChart(baseOption, configs, options, isPreview) {
      const mockData = generateMockChartData('line');
      return {
        ...baseOption,
        xAxis: {
          type: 'category',
          data: mockData.categories,
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        yAxis: {
          type: 'value',
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        series: [{
          name: '数据系列',
          type: 'line',
          data: mockData.values,
          smooth: options.smooth || false,
          lineStyle: {
            width: options.lineWidth || 2
          },
          label: {
            show: options.showDataLabels || false
          }
        }]
      };
    }

    // 生成柱状图
    function generateBarChart(baseOption, configs, options, isPreview) {
      const mockData = generateMockChartData('bar');
      return {
        ...baseOption,
        xAxis: {
          type: 'category',
          data: mockData.categories,
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        yAxis: {
          type: 'value',
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        series: [{
          name: '数据系列',
          type: 'bar',
          data: mockData.values,
          barWidth: `${options.barWidth || 60}%`,
          label: {
            show: options.showDataLabels || false
          }
        }]
      };
    }

    // 生成饼图
    function generatePieChart(baseOption, configs, options, isPreview) {
      const mockData = generateMockChartData('pie');
      return {
        ...baseOption,
        series: [{
          name: '数据分布',
          type: 'pie',
          radius: `${options.radius || 60}%`,
          data: mockData.pieData,
          roseType: options.roseType !== 'none' ? options.roseType : false,
          label: {
            show: true,
            formatter: options.showPercent ? '{b}: {c} ({d}%)' : '{b}: {c}'
          }
        }]
      };
    }

    // 生成散点图
    function generateScatterChart(baseOption, configs, options, isPreview) {
      const mockData = generateMockChartData('scatter');
      return {
        ...baseOption,
        xAxis: {
          type: 'value',
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        yAxis: {
          type: 'value',
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        series: [{
          name: '数据点',
          type: 'scatter',
          data: mockData.scatterData,
          symbolSize: options.symbolSize || 8
        }]
      };
    }

    // 生成热力图
    function generateHeatmapChart(baseOption, configs, options, isPreview) {
      const mockData = generateMockChartData('heatmap');
      return {
        ...baseOption,
        grid: {
          height: '50%',
          top: '10%'
        },
        xAxis: {
          type: 'category',
          data: mockData.xCategories,
          splitArea: {
            show: true
          },
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        yAxis: {
          type: 'category',
          data: mockData.yCategories,
          splitArea: {
            show: true
          },
          axisLabel: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        visualMap: {
          min: 0,
          max: 10,
          calculable: true,
          orient: 'horizontal',
          left: 'center',
          bottom: '15%',
          textStyle: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--text-color').trim()
          }
        },
        series: [{
          name: '热力值',
          type: 'heatmap',
          data: mockData.heatmapData,
          label: {
            show: true
          }
        }]
      };
    }

    // 生成默认图表
    function generateDefaultChart(baseOption, chartType, isPreview) {
      return {
        ...baseOption,
        series: [{
          name: chartType,
          type: 'line',
          data: [120, 200, 150, 80, 70, 110, 130]
        }]
      };
    }

    // 生成模拟图表数据
    function generateMockChartData(chartType) {
      switch (chartType) {
        case 'line':
        case 'bar':
          return {
            categories: ['一月', '二月', '三月', '四月', '五月', '六月', '七月'],
            values: [120, 200, 150, 80, 70, 110, 130]
          };
        case 'pie':
          return {
            pieData: [{
              value: 1048,
              name: '搜索引擎'
            }, {
              value: 735,
              name: '直接访问'
            }, {
              value: 580,
              name: '邮件营销'
            }, {
              value: 484,
              name: '联盟广告'
            }, {
              value: 300,
              name: '视频广告'
            }]
          };
        case 'scatter':
          return {
            scatterData: Array.from({
              length: 50
            }, () => [Math.random() * 100, Math.random() * 100])
          };
        case 'heatmap':
          const xCategories = ['周一', '周二', '周三', '周四', '周五', '周六', '周日'];
          const yCategories = ['上午', '下午', '晚上'];
          const heatmapData = [];
          for (let i = 0; i < xCategories.length; i++) {
            for (let j = 0; j < yCategories.length; j++) {
              heatmapData.push([i, j, Math.round(Math.random() * 10)]);
            }
          }
          return {
            xCategories,
            yCategories,
            heatmapData
          };
        default:
          return {
            categories: [],
            values: []
          };
      }
    }

    // 生成模拟预览数据
    function generateMockPreviewData() {
      return Array.from({
        length: 10
      }, (_, index) => ({
        id: index + 1,
        name: `数据项 ${index + 1}`,
        value: Math.floor(Math.random() * 1000),
        category: ['A类', 'B类', 'C类'][Math.floor(Math.random() * 3)],
        date: new Date(2023, 0, index + 1).toLocaleDateString()
      }));
    }

    // 保存到历史记录
    function saveToHistory(config, option) {
      const historyItem = {
        id: Date.now().toString(),
        name: config.options.title || `${config.chartType} 图表`,
        type: config.chartType,
        config,
        option,
        createTime: new Date().toLocaleString(),
        thumbnail: generateThumbnail(option)
      };
      chartHistory.value.unshift(historyItem);

      // 限制历史记录数量
      if (chartHistory.value.length > 50) {
        chartHistory.value = chartHistory.value.slice(0, 50);
      }

      // 保存到本地存储
      localStorage.setItem('chartHistory', JSON.stringify(chartHistory.value));
    }

    // 生成缩略图
    function generateThumbnail(option) {
      // 简化版的图表配置用于缩略图显示
      return {
        ...option,
        title: {
          ...option.title,
          textStyle: {
            fontSize: 12
          }
        },
        legend: {
          show: false
        },
        tooltip: {
          show: false
        }
      };
    }

    // 预览图表历史
    function previewChart(historyItem) {
      chartOption.value = historyItem.option;
      showHistory.value = false;
    }

    // 删除图表历史
    function deleteChart(historyItem) {
      const index = chartHistory.value.findIndex(item => item.id === historyItem.id);
      if (index !== -1) {
        chartHistory.value.splice(index, 1);
        localStorage.setItem('chartHistory', JSON.stringify(chartHistory.value));
      }
    }

    // 初始化时加载历史记录
    function loadHistory() {
      try {
        const saved = localStorage.getItem('chartHistory');
        if (saved) {
          chartHistory.value = JSON.parse(saved);
        }
      } catch (error) {
        console.error('加载图表历史失败:', error);
      }
    }

    // 页面加载时执行
    loadHistory();
    const __returned__ = {
      selectedChartType,
      chartOption,
      showFileUpload,
      UploadFiles,
      showDataPreview,
      currentDataFile,
      previewData,
      showFileStructure,
      workspaceFiles,
      showHistory,
      chartHistory,
      DataPreview,
      openHistory,
      onChartTypeSelect,
      handleWorkspaceUpdate,
      handleWorkspaceRemove,
      handleWorkspacePreview,
      handleWorkspaceClear,
      handleShowFileStructure,
      handleColumnDrag,
      handleGenerateChart,
      handlePreviewChart,
      generateChartOption,
      generateLineChart,
      generateBarChart,
      generatePieChart,
      generateScatterChart,
      generateHeatmapChart,
      generateDefaultChart,
      generateMockChartData,
      generateMockPreviewData,
      saveToHistory,
      generateThumbnail,
      previewChart,
      deleteChart,
      loadHistory,
      TopBar,
      SideBar,
      ChartTypeSelector,
      DataMappingConfig,
      ChartDisplay,
      ChartHistoryModal,
      FileUploadModal,
      DataPreviewModal,
      FileWorkspace,
      FileStructurePanel,
      get getFilePreview() {
        return getFilePreview;
      },
      ref,
      nextTick
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
