/* eslint-disable */

// 矩形树图图生成器
export default function treemapOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    return {
        title: {
            text: config.title || 'Treemap Chart',
            subtext: config.subtext || '',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold',
            },
            subtextStyle: {
                fontSize: 12,
            },
        },
        tooltip: {
            trigger: 'item',
            formatter: '{b}: {c} ({d}%)',
        },
        series: [
            {
                type: 'treemap',
                layoutAlgorithm: 'squarify',
                data: seriesData,
                label: {
                    show: true,
                    formatter: '{b}: {c} ({d}%)',
                },
                itemStyle: {
                    borderColor: '#fff',
                    borderWidth: 1,
                },
            },
        ],
    }
}
