    },
    Pie: {
        mapping: [
            { key: 'category', label: 'Category', type: ['string', 'category'], required: true },
            { key: 'value', label: 'Value', type: ['number'], required: true }
        ],
        advanced: [
            { key: 'title', label: 'Chart Title', type: 'text', placeholder: 'Enter chart title' },
