            { key: 'animation', label: 'Animation', type: 'checkbox' }
        ],
        filter: [
            { key: 'dataRange', label: '数据范围', type: 'select', options: ['all', 'first100', 'last100', 'sample'] },
            { key: 'nullHandling', label: '空值处理', type: 'select', options: ['ignore', 'zero'] },
            { key: 'groupSmall', label: '小类合并为其他', type: 'checkbox' }
        ]
    },
    Scatter: {
        mapping: [
            { key: 'xAxis', label: 'X Axis', type: ['number'], required: true },
            { key: 'yAxis', label: 'Y Axis', type: ['number'], required: true },
            { key: 'series', label: 'Series', type: ['string','category'], required: false, multiple: true }
        ],
        advanced: [
            { key: 'title', label: 'Chart Title', type: 'text', placeholder: 'Enter chart title' },
            { key: 'colorScheme', label: 'Color Theme', type: 'select', options: ['default','blue','green','warm','cool'] },
            { key: 'animation', label: 'Animation', type: 'checkbox' }
        ],
        filter: [
            { key: 'dataRange', label: '数据范围', type: 'select', options: ['all', 'first100', 'last100', 'sample'] },
            { key: 'nullHandling', label: '空值处理', type: 'select', options: ['ignore', 'zero', 'interpolate'] },
            { key: 'outlier', label: '剔除异常值', type: 'checkbox' }
        ]
    },
    Radar: {
        mapping: [
            { key: 'indicator', label: 'Indicator', type: ['string','category'], required: true },
            { key: 'value', label: 'Value', type: ['number'], required: true, multiple: true }
        ],
        advanced: [
            { key: 'title', label: 'Chart Title', type: 'text', placeholder: 'Enter chart title' },
            { key: 'colorScheme', label: 'Color Theme', type: 'select', options: ['default','blue','green','warm','cool'] },
            { key: 'animation', label: 'Animation', type: 'checkbox' }
        ],
        filter: [
            { key: 'dataRange', label: '数据范围', type: 'select', options: ['all', 'first100', 'last100', 'sample'] },
            { key: 'nullHandling', label: '空值处理', type: 'select', options: ['ignore', 'zero', 'interpolate'] }
        ]
    },
  // ...可继续补充其他类型
