/* eslint-disable */

// 旭日图生成器
export default function sunburstOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 默认旭日图 option
    return {
        tooltip: {
            trigger: 'item',
            formatter: info => {
                // 显示名称和数值
                return `${info.name} : ${info.value}`;
            }
        },
        series: [
            {
                type: 'sunburst',
                data: seriesData,
                radius: [0, '90%'],
                sort: null,
                emphasis: {
                    focus: 'ancestor' //
                },
                label: {
                    rotate: 'radial',
                    fontSize: 12
                },
                levels: [
                    {},
                    {
                        r0: '0%', r: '30%',
                        label: { rotate: 0 }
                    },
                    {
                        r0: '30%', r: '60%',
                        label: { align: 'right' }
                    },
                    {
                        r0: '60%', r: '90%',
                        label: { align: 'right' }
                    }
                ],
                ...customOption
            }
        ]
    };
}
