/* eslint-disable */

// 仪表盘配置生成器
export default function gaugeOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const gaugeData = Array.isArray(seriesData)
        ? seriesData
        : (seriesData.name && seriesData.value)
            ? seriesData.name.map((n, i) => ({ name: n, value: seriesData.value[i] }))
            : [];
    // 支持对象格式和数组格式
    let gaugeArr = Array.isArray(seriesData)
        ? seriesData
        : (seriesData.name && seriesData.value)
            ? seriesData.name.map((n, i) => ({ name: n, value: seriesData.value[i] }))
            : [];

    // 自动拆分为多个仪表盘，每个仪表盘只显示一个指针
    const series = gaugeArr.map((item, idx) => ({
        name: item.name,
        type: 'gauge',
        detail: {
            formatter: '{value}'
        },
        data: [item],
        // 自动布局：每行最多2个仪表盘
        center: [
            `${25 + 50 * (idx % 2)}%`,
            `${30 + 40 * Math.floor(idx / 2)}%`
        ],
        radius: '35%'
    }));

    return {
        tooltip: {
            formatter: '{a} <br/>{b} : {c}%'
        },
        series
    };
};
