/* eslint-disable */

// 旭日图生成器
export default function sunburstOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    return {
        title: {
            text: config.title || 'Chart of Sunburst',
            subtext: config.subtext || '',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold',
            },
            subtextStyle: {
                fontSize: 12,
            }
        },
        tooltip: {
            trigger: 'item',
            formatter: info => `${info.name} : ${info.value}`
        },
        series: [
            {
                type: 'sunburst',
                data: seriesData,
                radius: [0, '95%'],
                sort: undefined,
                emphasis: {
                    focus: 'ancestor'
                },
                label: {
                    show: function (params) {
                        // 区域面积和角度双重判断
                        const rect = params.rect || params.labelRect;
                        // ECharts 5+ params 里有 angle
                        const angle = params.angle;
                        // 面积太小或角度太小都不显示
                        if ((rect && rect.width * rect.height < 400) || (typeof angle === 'number' && angle < 8)) {
                            return false;
                        }
                        return true;
                    },
                    fontSize: 12,
                    // 动态旋转：小角度区域不旋转
                    rotate: function (params) {
                        const angle = params.angle;
                        if (typeof angle === 'number' && angle < 12) {
                            return 0;
                        }
                        return 'tangential';
                    }
                },
                levels: [
                    {},
                    {
                        r0: '15%',
                        r: '35%',
                        itemStyle: { borderWidth: 2 },
                        label: {
                            rotate: function (params) {
                                const angle = params.angle;
                                if (typeof angle === 'number' && angle < 12) {
                                    return 0;
                                }
                                return 'tangential';
                            }
                        }
                    },
                    {
                        r0: '35%',
                        r: '70%',
                        label: { align: 'right' }
                    },
                    {
                        r0: '70%',
                        r: '72%',
                        label: { position: 'outside', padding: 3, silent: false },
                        itemStyle: { borderWidth: 3 }
                    }
                ],
                ...customOption
            }
        ]
    };
}
