import TopBar from '../components/TopBar.vue';
import SideBar from '../components/SideBar.vue';
import ChartTypeSelector from '../components/ChartIcon.vue';
import ChartDisplay from '../components/ChartDisplay.vue';
import ChartHistoryModal from '../components/ChartHistoryModal.vue';
import FileUploadModal from '../components/FileUploadModal.vue';
import DataPreviewModal from '../components/DataPreviewModal.vue';
import FileWorkspace from '../components/FileWorkspace.vue';
import FastStartPrompt from '../components/FastStartPrompt.vue';
import FileStructurePanel from '../components/FileStructurePanel.vue';
import DataMappingPanel from '../components/DataMappingPanel.vue';
import { getFilePreview } from '../services/FileServices.js';
import { DataStructureService, ChartConfigBuilder } from '../services/DataStructureService.js';
import { ref, nextTick } from 'vue';

// 文件上传相关

export default {
  __name: 'ChartVisualizations',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const showFileUpload = ref(false);
    function UploadFiles() {
      showFileUpload.value = true;
    }

    // 数据预览相关
    const showDataPreview = ref(false);
    const currentDataFile = ref(null);
    const previewData = ref([]);

    // 工作区相关
    const workspaceFiles = ref([]);

    // 图表类型选择
    const selectedChartType = ref('line');

    // 文件结构面板
    const showFileStructure = ref(false);
    const workspaceFilesWithStructure = ref([]);

    // 数据映射
    const currentMappingConfig = ref({});
    const isRealTimeRender = ref(true);

    // 测试图表绘制
    const chartOption = ref(null); // 改为null，显示FastStart

    // 图表存储区（可从后端获取）
    const chartHistory = ref([{
      title: '销售柱状图',
      option: {
        title: {
          text: '销售柱状图'
        },
        tooltip: {},
        xAxis: {
          data: ['一季度', '二季度', '三季度', '四季度']
        },
        yAxis: {},
        series: [{
          type: 'bar',
          data: [120, 200, 150, 80]
        }]
      }
    }, {
      title: '折线趋势',
      option: {
        title: {
          text: '折线趋势'
        },
        tooltip: {},
        xAxis: {
          data: ['1月', '2月', '3月', '4月']
        },
        yAxis: {},
        series: [{
          type: 'line',
          data: [30, 50, 80, 65]
        }]
      }
    }, {
      title: '饼图示例',
      option: {
        title: {
          text: '饼图示例'
        },
        tooltip: {},
        series: [{
          type: 'pie',
          radius: '60%',
          data: [{
            value: 40,
            name: 'A类'
          }, {
            value: 32,
            name: 'B类'
          }, {
            value: 28,
            name: 'C类'
          }]
        }]
      }
    }]);
    const showHistory = ref(false);
    function openHistory() {
      showHistory.value = true;
    }
    function previewChart(item) {
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = JSON.parse(JSON.stringify(item.option));
        showHistory.value = false;
      });
    }
    function deleteChart(idx) {
      chartHistory.value.splice(idx, 1);
    }

    // 图表选择
    function onChartTypeSelect(type) {
      console.log(`Selected chart type: ${type}`);
      selectedChartType.value = type;
      // 可添加处理为加载图表类型、切换组件等功能
    }

    // 显示文件结构面板
    function handleShowFileStructure(file) {
      console.log('Show file structure for:', file.name);

      // 模拟文件结构数据
      const fileWithStructure = {
        ...file,
        structure: {
          rows: 1000,
          columns: [{
            name: 'Date',
            type: 'date',
            samples: ['2023-01-01', '2023-01-02', '2023-01-03']
          }, {
            name: 'Sales',
            type: 'number',
            samples: [1200, 1500, 980]
          }, {
            name: 'Product',
            type: 'string',
            samples: ['Product A', 'Product B', 'Product C']
          }, {
            name: 'Category',
            type: 'string',
            samples: ['Electronics', 'Clothing', 'Books']
          }, {
            name: 'Price',
            type: 'number',
            samples: [29.99, 45.50, 12.99]
          }]
        }
      };
      workspaceFilesWithStructure.value = [fileWithStructure];
      showFileStructure.value = true;
    }

    // 列拖拽开始
    function handleColumnDragStart(data) {
      console.log('Column drag start:', data);
    }

    // 列拖拽结束
    function handleColumnDragEnd() {
      console.log('Column drag end');
    }

    // 数据映射变化
    function handleMappingChange(config) {
      console.log('Mapping changed:', config);
      currentMappingConfig.value = config;

      // 如果是实时渲染模式，自动生成图表
      if (isRealTimeRender.value && config.isValid) {
        generateChartFromMapping(config);
      }
    }

    // 渲染模式切换
    function handleRenderModeChange(isRealTime) {
      console.log('Render mode changed:', isRealTime);
      isRealTimeRender.value = isRealTime;
    }

    // 生成图表
    function handleGenerateChart(config) {
      console.log('Generate chart:', config);
      generateChartFromMapping(config);
    }

    // 从映射配置生成图表
    function generateChartFromMapping(config) {
      // 这里应该根据映射配置和数据生成ECharts配置
      // 现在先用一个示例
      const mockChartOption = {
        title: {
          text: `${selectedChartType.value} 图表`
        },
        tooltip: {},
        xAxis: {
          data: ['A', 'B', 'C', 'D']
        },
        yAxis: {},
        series: [{
          type: selectedChartType.value,
          data: [23, 45, 12, 37]
        }]
      };
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = mockChartOption;
      });
    }

    // 工作区相关方法
    function handleWorkspaceUpdate(files) {
      workspaceFiles.value = [...files];
      console.log('Workspace updated:', workspaceFiles.value.length, 'files');
    }
    function handleWorkspaceRemove(file) {
      console.log('File removed from workspace:', file.name);
      // 从工作区数组中移除文件
      const index = workspaceFiles.value.findIndex(f => f.id === file.id);
      if (index !== -1) {
        workspaceFiles.value.splice(index, 1);
      }
    }
    function handleWorkspacePreview(file) {
      // 预览工作区中的文件
      console.log('Preview workspace file:', file.name);
      currentDataFile.value = file;

      // 使用与 FileUploadModal 相同的预览逻辑
      if (file.previewData) {
        // 如果文件已经有预览数据，直接使用
        previewData.value = file.previewData;
        showDataPreview.value = true;
      } else {
        // 否则读取文件内容
        loadFilePreview(file);
      }
    }

    // 加载文件预览数据
    async function loadFilePreview(file) {
      try {
        const previewResult = await getFilePreview(file.id);
        previewData.value = previewResult.data;
        showDataPreview.value = true;
      } catch (error) {
        console.error('Preview failed:', error);
        alert('Preview failed: ' + error.message);
      }
    }
    function handleWorkspaceClear() {
      console.log('Workspace cleared');
      workspaceFiles.value = [];
    }
    const __returned__ = {
      showFileUpload,
      UploadFiles,
      showDataPreview,
      currentDataFile,
      previewData,
      workspaceFiles,
      selectedChartType,
      showFileStructure,
      workspaceFilesWithStructure,
      currentMappingConfig,
      isRealTimeRender,
      chartOption,
      chartHistory,
      showHistory,
      openHistory,
      previewChart,
      deleteChart,
      onChartTypeSelect,
      handleShowFileStructure,
      handleColumnDragStart,
      handleColumnDragEnd,
      handleMappingChange,
      handleRenderModeChange,
      handleGenerateChart,
      generateChartFromMapping,
      handleWorkspaceUpdate,
      handleWorkspaceRemove,
      handleWorkspacePreview,
      loadFilePreview,
      handleWorkspaceClear,
      TopBar,
      SideBar,
      ChartTypeSelector,
      ChartDisplay,
      ChartHistoryModal,
      FileUploadModal,
      DataPreviewModal,
      FileWorkspace,
      FastStartPrompt,
      FileStructurePanel,
      DataMappingPanel,
      get getFilePreview() {
        return getFilePreview;
      },
      get DataStructureService() {
        return DataStructureService;
      },
      get ChartConfigBuilder() {
        return ChartConfigBuilder;
      },
      ref,
      nextTick
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
