    visualMap,
    series: seriesArr,
    animation: animation,
    animationDuration: animation ? 1500 : 0,
    ...customOption
  };
  if (config.singleAxisMode === 'x' || config.singleAxisMode === 'y') {
    option.singleAxis = singleAxis;
