/* eslint-disable */

// 雷达图生成器
export default function radarOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    console.log('[radarOption] config:', config)
    console.log('[radarOption] fileDataMap:', fileDataMap)
    console.log('[radarOption] xData:', xData)
    console.log('[radarOption] yDataArr:', yDataArr)
    console.log('[radarOption] selectedCharType:', selectedChartType)
    console.log('[radarOption] seriesData:', seriesData)

    // 解包RadarPack
    const { indicator, seriesData_radar } = seriesData;

    return {
        // Echarts 雷达图配置
        title: {
            text: config.title || `Chart of Radar`,
            left: 'center',
            textStyle: { fontSize: 16, fontWeight: 'bold' }
        },
        legend: {
            type: 'scroll',
            show: config.legendVisible !== false,
            top: config.legendPosition || 'bottom',
            data: seriesData_radar.map(item)
        },
        radar: {
            indicator: indicator
        },
    }
}
