/* eslint-disable */

// 旭日图生成器
export default function sunburstOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    return {
        title: {
            text: config.title || 'Chart of Sunburst',
            subtext: config.subtext || '',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold'
            },
            subtextStyle: {
                fontSize: 12
            },
            left: 'center',
            top: 'top'
        },
        tooltip: {
            trigger: 'item',
            formatter: info => `${info.name} : ${info.value}`
        },
        series: [
            {
                type: 'sunburst',
                data: seriesData,
                radius: [0, '95%'],
                sort: undefined,
                emphasis: {
                    focus: 'ancestor'
                },
                label: {
                    show: function (params) {
                        const rect = params.rect || params.labelRect;
                        if (rect && rect.width * rect.height < 400) {
                            return false;
                        }
                        return true;
                    },
                    fontSize: 12,
                    minAngle: 8,
                },
                levels: [
                    {},
                    {
                        r0: '15%',
                        r: '35%',
                        itemStyle: { borderWidth: 2 },
                    },
                    {
                        r0: '35%',
                        r: '70%',
                    },
                    {
                        r0: '70%',
                        r: '72%',
                        label: { position: 'outside', padding: 3, silent: false, rotate: 'tangential' },
                        itemStyle: { borderWidth: 3 }
                    }
                ],
                ...customOption
            }
        ]
    };
}
