import { ref, watch, onMounted, onBeforeUnmount, computed } from 'vue';
import LineChartIcon from '../svg/LineChartIcon.vue';
import BarChartIcon from '../svg/BarChartIcon.vue';
import PieChartIcon from '../svg/PieChartIcon.vue';
import ScatterChartIcon from '../svg/ScatterChartIcon.vue';
import MapChartIcon from '../svg/MapChartIcon.vue';
import KlineChartIcon from '../svg/KlineChartIcon.vue';
import RadarChartIcon from '../svg/RadarChartIcon.vue';
import BoxplotChartIcon from '../svg/BoxplotChartIcon.vue';
import HeatmapChartIcon from '../svg/HeatmapChartIcon.vue';
import GraphChartIcon from '../svg/GraphChartIcon.vue';
import TreeChartIcon from '../svg/TreeChartIcon.vue';
import TreemapChartIcon from '../svg/TreemapChartIcon.vue';
import SunburstChartIcon from '../svg/SunburstChartIcon.vue';
import ParallelChartIcon from '../svg/ParallelChartIcon.vue';
import SankeyChartIcon from '../svg/SankeyChartIcon.vue';
import FunnelChartIcon from '../svg/FunnelChartIcon.vue';
import GaugeChartIcon from '../svg/GaugeChartIcon.vue';
import PictorialBarChartIcon from '../svg/PictorialBarChartIcon.vue';
import ThemeRiverChartIcon from '../svg/ThemeRiverChartIcon.vue';
import CalendarChartIcon from '../svg/CalendarChartIcon.vue';
import AnimateIcon from '../Common/AnimateIcon.vue';
import * as echarts from 'echarts';

// 主题缓存，避免污染echarts对象

export default {
  __name: 'ChartDisplay',
  props: {
    option: {
      type: Object,
      required: true
    },
    colorTheme: {
      type: String,
      default: 'default'
    }
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const currentIcon = computed(() => {
      const icon = iconList[iconIndex.value];
      return icon && typeof icon === 'object' ? icon : null;
    });
    /* eslint-disable */
    const themeCache = {};

    // 动态加载并注册主题
    async function loadAndRegisterTheme(themeName) {
      if (themeName === 'default') return;
      if (themeCache[themeName]) return;
      try {
        const res = await fetch(`/themes/${themeName}.json`);
        if (!res.ok) throw new Error('Theme file loads failed');
        const obj = await res.json();
        echarts.registerTheme(themeName, obj);
        themeCache[themeName] = {
          themeObj: obj
        };
      } catch (e) {
        console.warn('Theme load failed:', themeName, e);
      }
    }
    const props = __props;
    const chartRef = ref(null);
    let chartInstance = null;
    let resizeObserver = null;
    const hasSeries = computed(() => {
      return props.option && Array.isArray(props.option.series) && props.option.series.length > 0;
    });

    // 空状态图标轮播逻辑
    const iconList = [BarChartIcon, LineChartIcon, PieChartIcon, ScatterChartIcon, MapChartIcon, KlineChartIcon, RadarChartIcon, BoxplotChartIcon, HeatmapChartIcon, GraphChartIcon, TreeChartIcon, TreemapChartIcon, SunburstChartIcon, ParallelChartIcon, SankeyChartIcon, FunnelChartIcon, GaugeChartIcon, PictorialBarChartIcon, ThemeRiverChartIcon, CalendarChartIcon];
    const iconIndex = ref(0);
    const iconOpacity = ref(1);
    let iconTimer = null;
    let fadeTimer = null;
    function startIconLoop() {
      clearInterval(iconTimer);
      clearTimeout(fadeTimer);
      iconOpacity.value = 1;
      iconTimer = setInterval(() => {
        // 先淡出
        iconOpacity.value = 0;
        fadeTimer = setTimeout(() => {
          iconIndex.value = (iconIndex.value + 1) % iconList.length;
          iconOpacity.value = 1;
        }, 400);
      }, 3500);
    }

    // 获取对比色函数
    function getContrastColor(bg) {
      let r, g, b;
      if (!bg) return '#333';
      if (bg.startsWith('#')) {
        if (bg.length === 4) {
          r = parseInt(bg[1] + bg[1], 16);
          g = parseInt(bg[2] + bg[2], 16);
          b = parseInt(bg[3] + bg[3], 16);
        } else if (bg.length === 7) {
          r = parseInt(bg.slice(1, 3), 16);
          g = parseInt(bg.slice(3, 5), 16);
          b = parseInt(bg.slice(5, 7), 16);
        }
      } else if (bg.startsWith('rgb')) {
        const arr = bg.match(/\d+/g);
        if (arr && arr.length >= 3) {
          r = parseInt(arr[0]);
          g = parseInt(arr[1]);
          b = parseInt(arr[2]);
        }
      }
      if (r === undefined || g === undefined || b === undefined) return '#333';
      const luminance = 0.299 * r + 0.587 * g + 0.114 * b;
      return luminance > 180 ? '#222' : '#eee';
    }
    function getCssVar(name) {
      return getComputedStyle(document.documentElement).getPropertyValue(name).trim();
    }
    function isTransparent(bg) {
      if (!bg) return true;
      if (bg === 'transparent') return true;
      if (bg.startsWith('rgba')) {
        const arr = bg.match(/\d+(\.\d+)?/g);
        if (arr && arr.length === 4 && parseFloat(arr[3]) === 0) return true;
      }
      return false;
    }
    async function renderChart() {
      console.log('Start rendering chart');
      console.log('value of hasSeries:', hasSeries.value);
      if (!hasSeries.value) return;
      if (chartInstance) {
        chartInstance.dispose();
        chartInstance = null;
      }
      const themeName = props.colorTheme === 'default' ? null : props.colorTheme;
      if (props.colorTheme !== 'default') {
        await loadAndRegisterTheme(props.colorTheme);
      }
      console.log('Rendering chart with theme:', themeName);
      chartInstance = echarts.init(chartRef.value, themeName);
      if (props.option) {
        // 动态处理x轴标签过长和过密
        const option = JSON.parse(JSON.stringify(props.option));
        // 自动适配标签颜色
        let bgColor = option.backgroundColor;
        if (!bgColor && props.colorTheme && props.colorTheme !== 'default') {
          const themeObj = themeCache[props.colorTheme] && themeCache[props.colorTheme].themeObj;
          if (themeObj && themeObj.backgroundColor) bgColor = themeObj.backgroundColor;
        }
        if (!bgColor || isTransparent(bgColor)) {
          bgColor = getCssVar('--bg-color') || '#fff';
        }
        let axisLabelColor = getContrastColor(bgColor);
        // 如果你想直接用主题色变量作为标签色，可用下行替换：
        // axisLabelColor = getCssVar('--text-color') || '#333'
        if (option.xAxis) {
          if (!option.xAxis.axisLabel) option.xAxis.axisLabel = {};
          option.xAxis.axisLabel.color = axisLabelColor;
        }
        if (option.yAxis) {
          if (!option.yAxis.axisLabel) option.yAxis.axisLabel = {};
          option.yAxis.axisLabel.color = axisLabelColor;
        }
        if (option.xAxis && option.xAxis.data && Array.isArray(option.xAxis.data)) {
          const labelCount = option.xAxis.data.length;
          if (!option.xAxis.axisLabel) option.xAxis.axisLabel = {};
          if (labelCount > 12) {
            option.xAxis.axisLabel.rotate = 45;
          }
          if (labelCount > 40) {
            option.xAxis.axisLabel.interval = Math.ceil(labelCount / 20);
          } else {
            option.xAxis.axisLabel.interval = 0;
          }
          option.xAxis.axisLabel.overflow = 'truncate';
          option.xAxis.axisLabel.width = 80;
          option.xAxis.axisLabel.ellipsis = '...';
        }
        console.log('[Rendering Theme]Setting chart option:', option);
        chartInstance.setOption(option, {
          notMerge: true,
          replaceMerge: ['series']
        });
      }
    }
    onMounted(() => {
      startIconLoop();
      renderChart();
      // 监听容器大小变化，自适应图表
      if (window.ResizeObserver) {
        resizeObserver = new ResizeObserver(() => {
          if (chartInstance) chartInstance.resize();
        });
        if (chartRef.value) resizeObserver.observe(chartRef.value);
      } else {
        window.addEventListener('resize', () => {
          if (chartInstance) chartInstance.resize();
        });
      }
    });
    onBeforeUnmount(() => {
      if (chartInstance) chartInstance.dispose();
      clearInterval(iconTimer);
      clearTimeout(fadeTimer);
      if (resizeObserver && chartRef.value) resizeObserver.unobserve(chartRef.value);
    });
    watch([() => props.option, () => props.colorTheme], renderChart, {
      deep: true
    });
    onMounted(renderChart);
    onBeforeUnmount(() => {
      if (chartInstance) chartInstance.dispose();
    });
    const __returned__ = {
      currentIcon,
      themeCache,
      loadAndRegisterTheme,
      props,
      chartRef,
      get chartInstance() {
        return chartInstance;
      },
      set chartInstance(v) {
        chartInstance = v;
      },
      get resizeObserver() {
        return resizeObserver;
      },
      set resizeObserver(v) {
        resizeObserver = v;
      },
      hasSeries,
      iconList,
      iconIndex,
      iconOpacity,
      get iconTimer() {
        return iconTimer;
      },
      set iconTimer(v) {
        iconTimer = v;
      },
      get fadeTimer() {
        return fadeTimer;
      },
      set fadeTimer(v) {
        fadeTimer = v;
      },
      startIconLoop,
      getContrastColor,
      getCssVar,
      isTransparent,
      renderChart,
      ref,
      watch,
      onMounted,
      onBeforeUnmount,
      computed,
      LineChartIcon,
      BarChartIcon,
      PieChartIcon,
      ScatterChartIcon,
      MapChartIcon,
      KlineChartIcon,
      RadarChartIcon,
      BoxplotChartIcon,
      HeatmapChartIcon,
      GraphChartIcon,
      TreeChartIcon,
      TreemapChartIcon,
      SunburstChartIcon,
      ParallelChartIcon,
      SankeyChartIcon,
      FunnelChartIcon,
      GaugeChartIcon,
      PictorialBarChartIcon,
      ThemeRiverChartIcon,
      CalendarChartIcon,
      AnimateIcon,
      get echarts() {
        return echarts;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
