/* eslint-disable */

// 折线图 option 生成器
export default function lineOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const { yAxis, title, animation } = config;
    const yArr = Array.isArray(yAxis) ? yAxis : [yAxis];

    const mergeMap = new Map(); // key: x标签, value: 各 y 数组的累加器
    xData.forEach((label, i) => {
        if (!mergeMap.has(label)) mergeMap.set(label, Array(yDataArr.length).fill(0));
        yDataArr.forEach((yList, idx) => {
            mergeMap.get(label)[idx] += Number(yList[i]) || 0;
        });
    });

    const uniqueXData = Array.from(mergeMap.keys());                 // 去重后的 x 轴
    const mergedY = Array.from({ length: yDataArr.length }, (_, idx) =>
        Array.from(mergeMap.values()).map(arr => arr[idx])
    );

    const seriesArr = yArr.map((y, idx) => ({
        name: y.field,
        type: 'line',
        data: mergedY[idx],
        animationDuration: animation ? 1500 : 0
    }));
    return {
        title: {
            text: title || `${yArr.map(y => y.field).join(',')} vs ${config.xAxis.field}`,
            left: 'center',
            textStyle: { fontSize: 16, fontWeight: 'bold' }
        },
        tooltip: { trigger: 'axis' },
        legend: {
            type: 'scroll',
            show: config.legendVisible !== false,
            data: yArr.map(y => y.field),
            top: config.legendPosition || 'bottom',
        },
        toolbox: {
            show: true,
            feature: {
                dataZoom: { show: true, yAxisIndex: 'none' },
                dataView: { show: true, readOnly: false },
                magicType: { show: true, type: ['line', 'bar'] },
                restore: { show: true },
                saveAsImage: { show: true },
            }
        },
        xAxis: {
            type: 'category',
            data: uniqueXData,
            axisLabel: { interval: 0, rotate: uniqueXData.length > 10 ? 45 : 0 }
        },
        yAxis: { type: 'value', name: yArr.map(y => y.field).join(',') },
        series: seriesArr,
        animation: animation,
        animationDuration: animation ? 1500 : 0,
        ...customOption
    };
}
