/**
 * 文件相关工具函数
 */
/* eslint-disable */
import { SUPPORTED_FILE_TYPES, STATUS_TEXT_MAP, STATUS_ICON_MAP } from '../constants/fileConstants.js'

// 格式化文件大小
export function formatFileSize(bytes) {
    if (!bytes || bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i]
}

// 获取文件状态文本
export function getStatusText(status) {
    return STATUS_TEXT_MAP[status] || '未知'
}

// 获取文件状态图标
export function getStatusIcon(status) {
    return STATUS_ICON_MAP[status] || '❓'
}

// 验证文件格式
export function isValidFileType(filename) {
    const ext = filename.split('.').pop().toLowerCase()
    return SUPPORTED_FILE_TYPES.includes(ext)
}
