export default {
  __name: 'ChartFilterConfig',
  props: {
    // 传入当前映射区的配置对象
    modelValue: {
      type: Object,
      required: true
    },
    rawData: {
      type: Array,
      default: () => []
    }
  },
  emits: ['update:modelValue'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();

    /* eslint-disable */
    const props = __props;
    const emit = __emit;
    const filters = ref([]);
    const logic = ref('AND');
    const collapsed = ref(false);
    const errorMsg = ref('');

    // 移除 guessType 函数，availableFields 只用 type
    const availableFields = computed(() => {
      const fields = [];
      const used = new Set();
      // 直接用 props.rawData
      let allRows = props.rawData || [];
      Object.values(props.modelValue).forEach(val => {
        if (Array.isArray(val)) {
          val.forEach(f => {
            if (f && f.field && !used.has(f.field)) {
              let values = f.values;
              if (!values && (f.type === 'category' || f.type === 'string') && allRows.length) {
                values = Array.from(new Set(allRows.map(row => row[f.field]).filter(v => v !== undefined && v !== null && v !== '')));
              }
              fields.push({
                name: f.field,
                label: f.field,
                type: f.type,
                values
              });
              used.add(f.field);
            }
          });
        } else if (val && typeof val === 'object' && val.field && !used.has(val.field)) {
          let values = val.values;
          if (!values && (val.type === 'category' || val.type === 'string') && allRows.length) {
            values = Array.from(new Set(allRows.map(row => row[val.field]).filter(v => v !== undefined && v !== null && v !== '')));
          }
          fields.push({
            name: val.field,
            label: val.field,
            type: val.type,
            values
          });
          used.add(val.field);
        }
      });
      // 调试：输出所有可用字段及类型
      console.log('[FilterPanel] availableFields:', fields);
      return fields;
    });

    // 初始化过滤器
    function addFilter() {
      // 默认选第一个字段并自动推断类型
      const firstField = availableFields.value[0];
      filters.value.push({
        id: Date.now() + Math.random(),
        field: firstField ? firstField.name : '',
        operator: '',
        value: '',
        valueMin: '',
        valueMax: '',
        type: firstField ? firstField.type : ''
      });
    }
    function removeFilter(idx) {
      filters.value.splice(idx, 1);
    }

    // 获取操作符列表
    function getOperators(type) {
      switch (type) {
        case 'number':
        case 'integer':
          return [{
            value: 'eq',
            label: '='
          }, {
            value: 'ne',
            label: '≠'
          }, {
            value: 'gt',
            label: '>'
          }, {
            value: 'ge',
            label: '≥'
          }, {
            value: 'lt',
            label: '<'
          }, {
            value: 'le',
            label: '≤'
          }];
        case 'string':
          return [{
            value: 'eq',
            label: '等于'
          }, {
            value: 'ne',
            label: '不等于'
          }, {
            value: 'contains',
            label: '包含'
          }, {
            value: 'notcontains',
            label: '不包含'
          }, {
            value: 'startsWith',
            label: '开头是'
          }, {
            value: 'endsWith',
            label: '结尾是'
          }];
        case 'category':
          return [{
            value: 'eq',
            label: '='
          }, {
            value: 'ne',
            label: '≠'
          }, {
            value: 'in',
            label: '包含于'
          }, {
            value: 'notin',
            label: '不包含于'
          }];
        case 'boolean':
          return [{
            value: 'eq',
            label: '等于'
          }, {
            value: 'ne',
            label: '不等于'
          }];
        case 'date':
          return [{
            value: 'eq',
            label: '='
          }, {
            value: 'ne',
            label: '≠'
          }, {
            value: 'before',
            label: '早于'
          }, {
            value: 'after',
            label: '晚于'
          }];
        default:
          return [{
            value: 'eq',
            label: '='
          }, {
            value: 'ne',
            label: '≠'
          }];
      }
    }

    // 获取分类值
    function getCategoryValues(field) {
      const f = availableFields.value.find(f => f.name === field);
      console.log('[FilterPanel] getCategoryValues for field:', field, 'Values:', f ? f.values : '[]');
      return f && f.values ? f.values : [];
    }

    // 监听 modelValue 的变化，初始化过滤器
    function onFieldChange(filter) {
      const f = availableFields.value.find(fld => fld.name === filter.field);
      filter.type = f ? f.type : '';
      filter.operator = '';
      filter.value = '';
      filter.valueMin = '';
      filter.valueMax = '';
      // 调试：输出当前选中字段的详细信息
      console.log('[FilterPanel] selected field:', f);
    }

    // 校验过滤器
    function validate() {
      for (const f of filters.value) {
        if (!f.field || !f.operator || f.value === '' || f.value === undefined) {
          errorMsg.value = 'Please complete all filter conditions';
          return false;
        }
      }
      errorMsg.value = '';
      return true;
    }

    // 移除 applyFilters 相关逻辑，改为 watch filters 自动 emit
    watch([filters, logic], () => {
      // 校验通过才 emit
      if (validate()) {
        emit('update:modelValue', {
          ...props.modelValue,
          filter: {
            logic: logic.value,
            filters: filters.value.map(f => ({
              field: f.field,
              operator: f.operator,
              value: f.value,
              type: f.type
            }))
          }
        });
      }
    }, {
      deep: true
    });
    const __returned__ = {
      props,
      emit,
      filters,
      logic,
      collapsed,
      errorMsg,
      availableFields,
      addFilter,
      removeFilter,
      getOperators,
      getCategoryValues,
      onFieldChange,
      validate,
      ref,
      computed,
      watch
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
