  }
}

/**
 * 转换为散点图配置
 */
function convertToScatterChart(basicConfig, advancedConfig, data) {
  const xData = basicConfig.x?.[0] ? data.map(row => row[`x_${basicConfig.x[0].columnName}`]) : []
  const yData = basicConfig.y?.[0] ? data.map(row => row[`y_${basicConfig.y[0].columnName}`]) : []

  const scatterData = xData.map((x, index) => [x, yData[index]])

  return {
    title: {
      text: '散点图',
      left: 'center'
    },
    tooltip: {
      trigger: 'item'
    },
    xAxis: {
      type: 'value'
    },
    yAxis: {
      type: 'value'
    },
    series: [{
      name: '数据点',
      type: 'scatter',
      data: scatterData
    }]
  }
}

