import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import TopBar from '../components/TopBar.vue';
import SideBar from '../components/SideBar.vue';
import ChartTypeSelector from '../components/chartIcons.vue';
import ChartDisplay from '../components/ChartDisplay.vue';
import ChartHistoryModal from '../components/ChartHistoryModal.vue';
import FileUploadModal from '../components/FileUploadModal.vue';
import DataPreviewModal from '../components/DataPreviewModal.vue';
import { fileService, chartService } from '../services/api.js';
import { ref, nextTick, onMounted } from 'vue';

// 文件上传相关

export default {
  __name: 'ChartVisualizations',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const showFileUpload = ref(false);
    function UploadFiles() {
      showFileUpload.value = true;
    }

    // 数据预览相关
    const showDataPreview = ref(false);
    const currentDataFile = ref(null);
    const previewData = ref([]);
    const fileList = ref([]);

    // 组件挂载时加载数据
    onMounted(async () => {
      await loadFiles();
      await loadChartHistory();
    });

    // 加载文件列表
    async function loadFiles() {
      try {
        const response = await fileService.getFiles();
        if (response.success) {
          fileList.value = response.files;
        }
      } catch (error) {
        console.error('加载文件列表失败:', error);
      }
    }

    // 加载图表历史
    async function loadChartHistory() {
      try {
        const response = await chartService.getCharts();
        if (response.success) {
          chartHistory.value = response.charts.map(chart => ({
            id: chart.id,
            title: chart.title,
            option: chart.chart_config
          }));
        }
      } catch (error) {
        console.error('加载图表历史失败:', error);
      }
    }
    async function DataPreview() {
      try {
        // 获取第一个文件进行预览，或者可以让用户选择
        if (fileList.value.length > 0) {
          const firstFile = fileList.value[0];
          currentDataFile.value = {
            id: firstFile.id,
            name: firstFile.original_name
          };

          // 获取文件预览数据
          const response = await fileService.previewFile(firstFile.id, 10);
          if (response.success) {
            previewData.value = response.preview_data;
          }
        } else {
          // 如果没有文件，使用示例数据
          currentDataFile.value = {
            name: 'No files uploaded'
          };
          previewData.value = [{
            column1: 'A',
            column2: 100
          }, {
            column1: 'B',
            column2: 200
          }, {
            column1: 'C',
            column2: 300
          }];
        }
        showDataPreview.value = true;
      } catch (error) {
        console.error('预览数据失败:', error);
        // 使用示例数据作为后备
        currentDataFile.value = {
          name: 'currentFile.csv'
        };
        previewData.value = [{
          column1: 'A',
          column2: 100
        }, {
          column1: 'B',
          column2: 200
        }, {
          column1: 'C',
          column2: 300
        }];
        showDataPreview.value = true;
      }
    }

    // 测试图表绘制
    const chartOption = ref({
      title: {
        text: '示例图表'
      },
      tooltip: {},
      xAxis: {
        data: ['A', 'B', 'C', 'D']
      },
      yAxis: {},
      series: [{
        type: 'line',
        data: [23, 45, 12, 37]
      }]
    });

    // 图表存储区（可从后端获取）
    const chartHistory = ref([{
      title: '销售柱状图',
      option: {
        title: {
          text: '销售柱状图'
        },
        tooltip: {},
        xAxis: {
          data: ['一季度', '二季度', '三季度', '四季度']
        },
        yAxis: {},
        series: [{
          type: 'bar',
          data: [120, 200, 150, 80]
        }]
      }
    }, {
      title: '折线趋势',
      option: {
        title: {
          text: '折线趋势'
        },
        tooltip: {},
        xAxis: {
          data: ['1月', '2月', '3月', '4月']
        },
        yAxis: {},
        series: [{
          type: 'line',
          data: [30, 50, 80, 65]
        }]
      }
    }, {
      title: '饼图示例',
      option: {
        title: {
          text: '饼图示例'
        },
        tooltip: {},
        series: [{
          type: 'pie',
          radius: '60%',
          data: [{
            value: 40,
            name: 'A类'
          }, {
            value: 32,
            name: 'B类'
          }, {
            value: 28,
            name: 'C类'
          }]
        }]
      }
    }]);
    const showHistory = ref(false);
    function openHistory() {
      showHistory.value = true;
    }
    function previewChart(item) {
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = JSON.parse(JSON.stringify(item.option));
        showHistory.value = false;
      });
    }
    function deleteChart(idx) {
      chartHistory.value.splice(idx, 1);
    }

    // 图表选择
    function onChartTypeSelect(type) {
      console.log(`Selected chart type: ${type}`);
      // 可添加处理为加载图表类型、切换组件等功能
    }
    const __returned__ = {
      showFileUpload,
      UploadFiles,
      showDataPreview,
      currentDataFile,
      previewData,
      fileList,
      loadFiles,
      loadChartHistory,
      DataPreview,
      chartOption,
      chartHistory,
      showHistory,
      openHistory,
      previewChart,
      deleteChart,
      onChartTypeSelect,
      TopBar,
      SideBar,
      ChartTypeSelector,
      ChartDisplay,
      ChartHistoryModal,
      FileUploadModal,
      DataPreviewModal,
      get fileService() {
        return fileService;
      },
      get chartService() {
        return chartService;
      },
      ref,
      nextTick,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
