/* eslint-disable */

// 漏斗图配置生成器
export default function funnelOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const funnelData = seriesData;

    return {
        tooltip: {
            trigger: 'item',
            formatter: '{a} <br/>{b} : {c} ({d}%)'
        },
        series: [
            {
                name: '漏斗图',
                type: 'funnel',
                data: funnelData
            }
        ]
    };
}
