            type: 'piecewise',
            inRange: {
                color: visualColors
            },
            min,
            max,
            dimension: 0, // 默认映射第一个维度
            orient,
            left: 'center', // or 
            top: config.legendPosition || 'bottom',
        };
    } else {
        visualMap = {};
    }

    const option = {
        parallelAxis,
        parallel: {
            left: '5%',
            right: '13%',
            bottom: '10%',
            parallelAxisDefault: {
                type: 'value',
                nameLocation: 'end',
                nameGap: 20
            }
        },
        series,
        visualMap: config.legendVisible === false ? undefined : visualMap,
        ...customOption
    };

    return option;
}
