/* eslint-disable */

// 平行坐标系图配置生成器
// 平行坐标系图配置生成器
export default function parallelOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 兼容数据格式：seriesData 可能是对象（含 dimensions/data/name）或数组
    let dimensions = [];
    let data = [];
    let name = '';
    if (Array.isArray(seriesData)) {
        data = seriesData;
    } else if (seriesData && typeof seriesData === 'object') {
        dimensions = seriesData.dimensions || [];
        data = seriesData.data || [];
        name = seriesData.name || '';
    }

    // 自动生成 parallelAxis
    const parallelAxis = (dimensions.length > 0 ? dimensions : (yDataArr || [])).map((dim, idx) => {
        if (typeof dim === 'string') {
            return {
                dim: idx,
                name: dim,
                type: 'value'
            };
        } else if (typeof dim === 'object') {
            return {
                dim: idx,
                name: dim.name || `Dimension ${idx + 1}`,
                type: dim.type || 'value',
                ...dim
            };
        }
        return {
            dim: idx,
            name: `Dimension ${idx + 1}`,
            type: 'value'
        };
    });

    const option = {
        tooltip: {
            trigger: 'item',
            axisPointer: {
                type: 'shadow'
            }
        },
        parallelAxis,
        parallel: {
            left: '5%',
            right: '13%',
            bottom: '10%',
            top: '10%',
            parallelAxisDefault: {
                type: 'value',
                nameLocation: 'end',
                nameGap: 20,
                axisLabel: {
                    formatter: '{value}'
                }
            }
        },
        series: [
            {
                type: 'parallel',
                name,
                data,
                smooth: true,
                lineStyle: {
                    width: 2
                }
            }
        ]
    };

    // 合并自定义配置
    Object.assign(option, customOption);

    return option
}
