/* eslint-disable */

// 平行坐标系图配置生成器
// 平行坐标系图配置生成器
export default function parallelOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 解包数据
    const { dimensions, name } = seriesData;

    if (!dimensions || !name || !Array.isArray(dimensions) || !Array.isArray(name)) {
        return {};
    }

    // 1. 获取所有维度名
    const dimensionNames = Object.keys(name[0]);

    // 2. 构造 parallelAxis
    const parallelAxis = dimensionNames.map((dim, idx) => {
        // 判断类型
        const isNumber = typeof name[0][dim] === 'number';
        return {
            dim: idx,
            name: dim,
            type: isNumber ? 'value' : 'category'
        };
    });

    // 3. 构造 series.data
    // dimensions 是二维数组，每个子数组是一组数据
    // 这里默认只用第一个（可根据实际需求调整）
    const data = (dimensions[0] || []).map(item => dimensionNames.map(dim => item[dim]));

    // 4. 组装 option
    const option = {
        parallelAxis,
        parallel: {
            left: '5%',
            right: '13%',
            bottom: '10%',
            parallelAxisDefault: {
                type: 'value',
                nameLocation: 'end',
                nameGap: 20
            }
        },
        series: [{
            type: 'parallel',
            lineStyle: {
                width: 2
            },
            data
        }],
        ...customOption
    };

    return option;
}
