/* eslint-disable */

// 桑基图配置生成器

export default function sankeyOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 解包
    const { data, links } = seriesData;

    return {
        title: {
            text: config.title || 'Chart of Sankey',
            subtext: config.subtext || '',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold',
            },
            subtextStyle: {
                fontSize: 12,
            },
        },
        tooltip: {
            trigger: 'item',
            triggerOn: 'mousemove'
        },
        toolbox: {
            show: true,
            feature: {
                restore: { show: true },
                saveAsImage: { show: true },
            }
        },
        series: [{
            type: 'sankey',
            emphasis: {
                focus: 'adjacency'
            },
            lineStyle: {
                width: config.lineWidth || 1,
                opacity: config.lineOpacity || 0.5,
                curveness: config.lineCurveness || 0.5
            },
            data: data,
            links: links,
        }],
        ...customOption
    }
}
