      }

      // 防止底部超出屏幕
      if (top + rect.height > viewportHeight) {
        top = event.pageY - rect.height - 10;
      }

      // 防止左边超出屏幕
      if (left < 0) {
        left = 10;
      }

      // 防止顶部超出屏幕
      if (top < 0) {
        top = 10;
      }
      tooltip.style = {
