/* eslint-disable */

/**
 * 高级配置项：
 * 树状图朝向 orient
 * 树状图显示样式 layout
 * 树状图多树排列形式 left, right, top, bottom
 * 曲线度 curveness
 * 线宽 lineWidth
 * 节点间距 nodeGap
 * 节点原点半径 symbolSize
 * 折线分叉位置 edgeForkPosition
 * 连线形状 edgeShape
 * 默认展开层级 initialTreeDepth
 */

// 树状图生成器
export default function treeOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    console.log('[TreeOption Gen] seriesData: ', seriesData);

    return {
        tooltip: {
            trigger: 'item',
            triggerOn: 'mousemove',
            formatter: function (params) {
                // 支持显示 value
                if (params.data.value !== undefined && params.data.value !== null) {
                    return `${params.data.name} : ${params.data.value}`;
                }
                return params.data.name;
            }
        },
        // 判断是否有多棵树
        series: Array.isArray(seriesData)
            ? seriesData.map((tree, idx, arr) => {
                const n = arr.length;
                // 横向均分 left/right
                const left = `${(idx * 100 / n + 5)}%`;
                const right = `${((n - idx - 1) * 100 / n + 5)}%`;
                return {
                    type: 'tree',
                    data: [tree],
                    orient: 'LR', // LR, RL, TB, BT
                    /**
                     * orthogonal: 直角布局, radial: 径向布局
                     * orthogonal 布局时, orient 只支持 LR, RL
                     * radial 布局时, orient 只支持 TB, BT
                     * 直角布局时，节点间距会更大一些
                     * 径向布局时，节点间距会更小一些
                     */
                    layout: 'orthogonal', // orthogonal, radial
                    top: '10%',
                    left,
                    bottom: '10%',
                    right,
                    symbolSize: 9,
                    label: {
                        position: 'left',
                        verticalAlign: 'middle',
                        align: 'right',
                        fontSize: 12,
                        overflow: 'break', // 标签自动换行
                        ellipsis: true
                    },
                    leaves: {
                        label: {
                            position: 'right',
                            verticalAlign: 'middle',
                            align: 'left',
                            overflow: 'break',
                            ellipsis: true
                        }
                    },
                    lineStyle: {
                        color: 'auto',
                        width: 1.5,
                        curveness: 0 // 曲线度
                    },
                    emphasis: {
                        focus: 'descendant'
                    },
                    symbolSize: 7,
                    edgeShape: 'curve', // curve, polyline
                    edgeForkPosition: '50%', // 折线分叉位置
                    expandAndCollapse: true,
                    animationDuration: 500,
                    animationDurationUpdate: 700,
                    initialTreeDepth: config.initialTreeDepth || 2,
                    roam: true
                };
            })
            : [
                {
                    type: 'tree',
                    data: seriesData,
                    orient: 'LR', // LR, RL, TB, BT
                    /**
                     * orthogonal: 直角布局, radial: 径向布局
                     * orthogonal 布局时, orient 只支持 LR, RL
                     * radial 布局时, orient 只支持 TB, BT
                     * 直角布局时，节点间距会更大一些
                     * 径向布局时，节点间距会更小一些
                     */
                    layout: 'orthogonal', // orthogonal, radial
                    top: '10%',
                    left: '10%',
                    bottom: '10%',
                    right: '10%',
                    symbolSize: 9,
                    label: {
                        position: 'left',
                        verticalAlign: 'middle',
                        align: 'right',
                        fontSize: 12,
                        overflow: 'break',
                        ellipsis: true
                    },
                    leaves: {
                        label: {
                            position: 'right',
                            verticalAlign: 'middle',
                            align: 'left',
                            overflow: 'break',
                            ellipsis: true
                        }
                    },
                    emphasis: {
                        focus: 'descendant'
                    },
                    expandAndCollapse: true,
                    animationDuration: 500,
                    animationDurationUpdate: 700,
                    initialTreeDepth: config.initialTreeDepth || 2,
                    roam: true
                }
            ],
        ...customOption,
    };
}
