import { ref, watch, onMounted, onBeforeUnmount } from 'vue';
import * as echarts from 'echarts';

/**
 * Props
 * @prop {Object} option - ECharts 配置对象，必填
 * @prop {String} colorTheme - 主题名，默认 'default'
 */

export default {
  __name: 'ChartThumbnail',
  props: {
    option: {
      type: Object,
      required: true
    },
    colorTheme: {
      type: String,
      default: 'default'
    }
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const props = __props;
    const thumbRef = ref(null);
    let chart = null;
    const themeCache = {};
    async function loadAndRegisterTheme(themeName) {
      if (themeCache[themeName]) return;
      try {
        const res = await fetch(`/themes/${themeName}.json`);
        if (!res.ok) throw new Error('Theme file loads failed');
        const obj = await res.json();
        echarts.registerTheme(themeName, obj);
        themeCache[themeName] = {
          themeObj: obj
        };
      } catch (e) {
        // 静默失败
      }
    }
    function getOptionWithoutTitle(option) {
      const opt = JSON.parse(JSON.stringify(option));
      delete opt.title;
      return opt;
    }
    async function render() {
      console.log('Rendering chart thumbnail with option:', props.option);
      if (!thumbRef.value) return;
      await loadAndRegisterTheme(props.colorTheme);
      if (!chart) {
        chart = echarts.init(thumbRef.value, props.colorTheme);
      }
      if (chart && props.option) {
        chart.setOption(getOptionWithoutTitle(props.option), true);
      }
    }
    watch(() => [props.option, props.colorTheme], render, {
      deep: true
    });
    onMounted(render);
    onBeforeUnmount(() => {
      if (chart) chart.dispose();
    });
    const __returned__ = {
      props,
      thumbRef,
      get chart() {
        return chart;
      },
      set chart(v) {
        chart = v;
      },
      themeCache,
      loadAndRegisterTheme,
      getOptionWithoutTitle,
      render,
      ref,
      watch,
      onMounted,
      onBeforeUnmount,
      get echarts() {
        return echarts;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
