/* eslint-disable */

// 树状图生成器
export default function treeOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    console.log('[TreeOption Gen] seriesData: ', seriesData);

    return {
        tooltip: {
            trigger: 'item',
            triggerOn: 'mousemove',
            formatter: function (params) {
                // 支持显示 value
                if (params.data.value !== undefined && params.data.value !== null) {
                    return `${params.data.name} : ${params.data.value}`;
                }
                return params.data.name;
            }
        },
        series: Array.isArray(seriesData)
            ? seriesData.map((tree, idx, arr) => {
                const n = arr.length;
                // 横向均分 left/right
                const left = `${(idx * 100 / n + 5)}%`;
                const right = `${((n - idx - 1) * 100 / n + 5)}%`;
                return {
                    type: 'tree',
                    data: [tree],
                    orient: 'LR', // LR, RL, 
                    top: '10%',
                    left,
                    bottom: '10%',
                    right,
                    symbolSize: 9,
                    label: {
                        position: 'left',
                        verticalAlign: 'middle',
                        align: 'right',
                        fontSize: 12,
                        overflow: 'break', // 标签自动换行
                        ellipsis: true
                    },
                    leaves: {
                        label: {
                            position: 'right',
                            verticalAlign: 'middle',
                            align: 'left',
                            overflow: 'break',
                            ellipsis: true
                        }
                    },
                    expandAndCollapse: true,
                    animationDuration: 500,
                    animationDurationUpdate: 700,
                    initialTreeDepth: config.initialTreeDepth || 2,
                    roam: true
                };
            })
            : [
                {
                    type: 'tree',
                    data: seriesData,
                    orient: 'LR',
                    top: '10%',
                    left: '10%',
                    bottom: '10%',
                    right: '10%',
                    symbolSize: 9,
                    label: {
                        position: 'left',
                        verticalAlign: 'middle',
                        align: 'right',
                        fontSize: 12,
                        overflow: 'break',
                        ellipsis: true
                    },
                    leaves: {
                        label: {
                            position: 'right',
                            verticalAlign: 'middle',
                            align: 'left',
                            overflow: 'break',
                            ellipsis: true
                        }
                    },
                    expandAndCollapse: true,
                    animationDuration: 500,
                    animationDurationUpdate: 700,
                    initialTreeDepth: config.initialTreeDepth || 2,
                    roam: true
                }
            ],
        ...customOption,
    };
}
