import { ref, computed } from 'vue';
// 动画参数

export default {
  __name: 'PieChartIcon',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */

    const percent = ref(0.68); // 主扇区占比（0~1），可随动画变化
    const circumference = 2 * Math.PI * 22;
    // 主扇区 dashoffset
    const dashOffset = computed(() => (1 - percent.value) * circumference);
    // 剩余扇区 dashoffset
    const dashOffsetRest = computed(() => -percent.value * circumference);

    // 动画循环：饼形占比从 0.1~0.85 之间来回变化
    let dir = 1;
    setInterval(() => {
      if (percent.value >= 0.85) dir = -1;
      if (percent.value <= 0.1) dir = 1;
      percent.value += dir * 0.012;
    }, 36);
    const __returned__ = {
      percent,
      circumference,
      dashOffset,
      dashOffsetRest,
      get dir() {
        return dir;
      },
      set dir(v) {
        dir = v;
      },
      ref,
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
