/* eslint-disable */

// 漏斗图配置生成器
export default function funnelOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const funnelData = seriesData;

    let seriesOption = {}
    if (true) {
        seriesOption = {
            series: [
                {
                    name: 'Funnel',
                    type: 'funnel',
                    sort: config.sort || 'descending',
                    label: {
                        show: true,
                        position: config.labelPosition || 'inside', // inside, left, right
                    },
                    labelLine: {
                        length: config.labelLineLength || 10,
                    },
                    emphasis: {
                        label: {
                            fontsize: 20,
                            fontWeight: 'bold',
                        }
                    },
                    data: funnelData
                }
            ]
        }
    }
    else {

    }

    return {
        title: {
            text: config.title || 'Chart of Funnel',
            subtext: config.subtext || '',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold',
            },
            subtextStyle: {
                fontSize: 12,
            },
        },
        tooltip: {
            trigger: 'item',
            formatter: '{a} <br/>{b} : {c} ({d}%)'
        },
        toolbox: {
            show: true,
            feature: {
                restore: { show: true },
                saveAsImage: { show: true },
            }
        },
        series: seriesOption,
    };
}
