/* eslint-disable */

// 平行坐标系图配置生成器
// 平行坐标系图配置生成器
export default function parallelOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 解包数据
    const { dimensions, name } = seriesData;

    if (!dimensions || !name || !Array.isArray(dimensions) || !Array.isArray(name)) {
        return {};
    }

    // 1. 获取所有维度名
    const dimensionNames = Object.keys(name[0]);

    // 2. 构造 parallelAxis
    const parallelAxis = dimensionNames.map((dim, idx) => {
        // 判断类型
        const isNumber = typeof name[0][dim] === 'number';
        return {
            dim: idx,
            name: dim,
            type: isNumber ? 'value' : 'category'
        };
    });

    // 3. 合并所有数据为一个数组
    const allData = dimensions.flat().map(item => dimensionNames.map(dim => item[dim]));

    // 4. 找到 category 维度索引
    const categoryDimIdx = dimensionNames.findIndex(dim => dim.toLowerCase() === 'category');

    // 5. 组装 option，使用 visualMap 以 category 上色
    const option = {
        parallelAxis,
        parallel: {
            left: '5%',
            right: '13%',
            bottom: '10%',
            parallelAxisDefault: {
                type: 'value',
                nameLocation: 'end',
                nameGap: 20
            }
        },
        series: [{
            type: 'parallel',
            lineStyle: {
                width: 2
            },
            data: allData
        }],
        visualMap: categoryDimIdx >= 0 ? [{
            type: 'piecewise',
            dimension: categoryDimIdx,
            categories: Array.from(new Set(allData.map(arr => arr[categoryDimIdx]))),
            orient: 'vertical',
            left: 'right',
            top: '10%',
            inRange: {
                color: ['#5470C6', '#91CC75', '#EE6666', '#FAC858', '#73C0DE', '#3BA272', '#FC8452', '#9A60B4', '#EA7CCC']
            },
            outOfRange: {
                color: ['#ccc']
            }
        }] : undefined,
        ...customOption
    };

    return option;
}
