// 折线图 option 生成器
export default function lineOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    // 网格线显示控制
    let xGrid = false, yGrid = false;
    switch (config.gridVisible) {
        case 'x':
            xGrid = true; yGrid = false; break;
        case 'y':
            xGrid = false; yGrid = true; break;
        case 'both':
            xGrid = true; yGrid = true; break;
        case 'none':
        default:
            xGrid = false; yGrid = false;
    }
    const { yAxis, title, animation, lineSeriesStyles } = config;
    const yArr = Array.isArray(yAxis) ? yAxis : [yAxis];
    // 处理每个系列的样式
    const seriesArr = yArr.map((y, idx) => {
        const base = {
            name: y.field,
            type: 'line',
            data: yDataArr[idx],
            smooth: config.isSmooth || false,
            animationDuration: animation ? 1500 : 0
        };
        // 读取 lineSeriesStyles
        if (Array.isArray(lineSeriesStyles) && lineSeriesStyles[idx]) {
            const style = lineSeriesStyles[idx];
            // 折线样式
            if (style.lineType) base.lineStyle = { ...(base.lineStyle || {}), type: style.lineType };
            if (style.lineColor) base.lineStyle = { ...(base.lineStyle || {}), color: style.lineColor };
            if (style.lineWidth) base.lineStyle = { ...(base.lineStyle || {}), width: style.lineWidth };
            // 数据点样式
            if (style.pointUnified) {
                // 统一样式
                base.symbol = style.pointSymbol || 'circle';
                if (style.pointColor) base.itemStyle = { ...(base.itemStyle || {}), color: style.pointColor };
                // 支持统一点大小
                if (typeof style.pointSize === 'number') {
                    base.symbolSize = style.pointSize;
                } else {
                    base.symbolSize = 8;
                }
            } else if (Array.isArray(style.points) && style.points.length > 0) {
                // 每个点单独样式
                base.symbol = function (value, params) {
                    const pt = style.points[params.dataIndex] || {};
                    return pt.symbol || 'circle';
                };
                base.itemStyle = {
                    ...(base.itemStyle || {}),
                    color: function (params) {
                        const pt = style.points[params.dataIndex] || {};
                        return pt.color || undefined;
                    }
                };
                base.symbolSize = function (value, params) {
                    const pt = style.points[params.dataIndex] || {};
                    return typeof pt.size === 'number' ? pt.size : 8;
                };
            }
        }
        return base;
    });
    return {
        title: {
            text: config.title || 'Chart of Line',
            subtext: config.subtext,
            left: config.titlePosition === 'left' ? 'left'
                : config.titlePosition === 'center' ? 'center'
                    : config.titlePosition === 'right' ? 'right'
                        : 'center', // bottom 也用 center
            top: config.titlePosition === 'bottom' ? 'bottom' : 'top',
            textStyle: { fontSize: 16, fontWeight: 'bold' },
            subtextStyle: { fontSize: 12 }
        },
        tooltip: { trigger: 'axis' },
        legend: {
            type: 'scroll',
            show: config.legendVisible !== false,
            data: yArr.map(y => y.field),
            top: config.legendPosition || 'bottom',
        },
        toolbox: {
            show: true,
            feature: {
                dataZoom: { show: true },
                dataView: { show: true, readOnly: false },
                magicType: { show: true, type: ['line', 'bar'] },
                restore: { show: true },
                saveAsImage: { show: true }
            }
        },
        xAxis: {
            type: 'category',
            data: xData,
            axisLabel: { interval: 0, rotate: xData.length > 10 ? 45 : 0 },
            splitLine: { show: xGrid }
        },
        yAxis: {
            type: 'value',
            name: yArr.map(y => y.field).join(','),
            splitLine: { show: yGrid }
        },
        series: seriesArr,
        animation: animation,
        animationDuration: animation ? 1500 : 0,
        ...customOption
    };
