import TopBar from '../components/TopBar.vue';
import SideBar from '../components/SideBar.vue';
import ChartTypeSelector from '../components/ChartIcon.vue';
import ChartDisplay from '../components/ChartDisplay.vue';
import ChartHistoryModal from '../components/ChartHistoryModal.vue';
import FileUploadModal from '../components/FileUploadModal.vue';
import DataPreviewModal from '../components/DataPreviewModal.vue';
import FileWorkspace from '../components/FileWorkspace.vue';
import FileStructurePanel from '../components/FileStructurePanel.vue';
import ChartConfigPanel from '../components/ChartConfigPanel.vue';
import { getFilePreview } from '../assets/JS/services/FileServices.js';
import { ref, nextTick, computed } from 'vue';

// 文件上传相关

export default {
  __name: 'ChartVisualizations',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const showFileUpload = ref(false);
    function UploadFiles() {
      showFileUpload.value = true;
    }

    // 数据预览相关
    const showDataPreview = ref(false);
    const currentDataFile = ref(null);
    const previewData = ref([]);

    // 全局文件数据结构优化
    // workspaceFiles: [{ name, id, size, status, parsedData: [...] }, ...]
    const workspaceFiles = ref([]);
    // 构建文件名到数据的映射，便于多文件查找
    const fileDataMap = computed(() => {
      const map = {};
      workspaceFiles.value.forEach(f => {
        map[f.name] = f.parsedData;
      });
      return map;
    });

    // 文件结构面板相关
    const showStructurePanel = ref(false);
    const currentStructureFile = ref(null);

    // 图表配置相关
    const selectedChartType = ref('Bar'); // 使用首字母大写格式与chartIcons.js一致
    const chartConfig = ref(null);

    // 图表存储区（可从后端获取）
    const chartHistory = ref([{
      title: '销售柱状图',
      option: {
        title: {
          text: '销售柱状图'
        },
        tooltip: {},
        xAxis: {
          data: ['一季度', '二季度', '三季度', '四季度']
        },
        yAxis: {},
        series: [{
          type: 'bar',
          data: [120, 200, 150, 80]
        }]
      }
    }, {
      title: '折线趋势',
      option: {
        title: {
          text: '折线趋势'
        },
        tooltip: {},
        xAxis: {
          data: ['1月', '2月', '3月', '4月']
        },
        yAxis: {},
        series: [{
          type: 'line',
          data: [30, 50, 80, 65]
        }]
      }
    }, {
      title: '饼图示例',
      option: {
        title: {
          text: '饼图示例'
        },
        tooltip: {},
        series: [{
          type: 'pie',
          radius: '60%',
          data: [{
            value: 40,
            name: 'A类'
          }, {
            value: 32,
            name: 'B类'
          }, {
            value: 28,
            name: 'C类'
          }]
        }]
      }
    }]);
    const showHistory = ref(false);
    function openHistory() {
      showHistory.value = true;
    }
    function previewChart(item) {
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = JSON.parse(JSON.stringify(item.option));
        showHistory.value = false;
      });
    }
    function deleteChart(idx) {
      chartHistory.value.splice(idx, 1);
    }

    // 图表选择
    function onChartTypeSelect(type) {
      console.log(`Selected chart type: ${type}`);
      selectedChartType.value = type;
      // 可添加处理为加载图表类型、切换组件等功能
    }

    // 工作区相关方法
    function handleWorkspaceUpdate(files) {
      workspaceFiles.value = [...files];
      console.log('Workspace updated:', workspaceFiles.value.length, 'files');
      // 调试：输出全局文件数据结构
      console.log('workspaceFiles:', JSON.parse(JSON.stringify(workspaceFiles.value)));
      console.log('fileDataMap:', JSON.parse(JSON.stringify(fileDataMap.value)));
    }
    function handleWorkspaceRemove(file) {
      console.log('File removed from workspace:', file.name);
      // 从工作区数组中移除文件
      const index = workspaceFiles.value.findIndex(f => f.id === file.id);
      if (index !== -1) {
        workspaceFiles.value.splice(index, 1);
      }
    }
    function handleWorkspacePreview(file) {
      // 预览工作区中的文件
      console.log('Preview workspace file:', file.name);
      currentDataFile.value = file;

      // 使用与 FileUploadModal 相同的预览逻辑
      if (file.previewData) {
        // 如果文件已经有预览数据，直接使用
        previewData.value = file.previewData;
        showDataPreview.value = true;
      } else {
        // 否则读取文件内容
        loadFilePreview(file);
      }
    }

    // 加载文件预览数据
    async function loadFilePreview(file) {
      try {
        const previewResult = await getFilePreview(file.id);
        previewData.value = previewResult.data;
        showDataPreview.value = true;
      } catch (error) {
        console.error('Preview failed:', error);
        alert('Preview failed: ' + error.message);
      }
    }
    function handleWorkspaceClear() {
      console.log('Workspace cleared');
      workspaceFiles.value = [];
    }

    // 文件结构面板处理方法
    function handleShowStructure(file) {
      console.log('Showing structure for file:', file.name);
      currentStructureFile.value = file;
      showStructurePanel.value = true;
    }
    function handleStructureMinimize(isMinimized) {
      console.log('Structure panel minimized:', isMinimized);
    }
    function handleColumnDrag(dragInfo) {
      console.log('Column drag event:', dragInfo);
      // 这里将来会处理列拖拽到图表配置的逻辑
      if (dragInfo.action === 'start') {
        console.log('Started dragging column:', dragInfo.column.name);
      } else if (dragInfo.action === 'end') {
        console.log('Finished dragging column');
      }
    }

    // 图表配置处理方法

    function handleConfigChange(config) {
      console.log('Chart config changed:', JSON.parse(JSON.stringify(config)));
      chartConfig.value = config;
      // 调试：输出当前 chartConfig
      console.log('当前 chartConfig:', JSON.parse(JSON.stringify(chartConfig.value)));
    }
    function handleGenerateChart(config) {
      console.log('Generating chart with config:', JSON.parse(JSON.stringify(config)));
      // 调试：输出 fileDataMap
      console.log('fileDataMap at generate:', JSON.parse(JSON.stringify(fileDataMap.value)));
      // 检查是否有用到的文件
      const fields = [];
      if (config.xAxis) fields.push(config.xAxis);
      if (Array.isArray(config.yAxis)) {
        config.yAxis.forEach(y => fields.push(y));
      } else if (config.yAxis) {
        fields.push(config.yAxis);
      }
      // 可扩展 series、color 等
      // 取所有用到的文件名
      const filesNeeded = Array.from(new Set(fields.map(f => f.file)));
      // 检查数据
      const missing = filesNeeded.filter(f => !fileDataMap.value[f]);
      if (missing.length > 0) {
        alert('请先上传并解析所有用到的数据文件：' + missing.join(','));
        return;
      }
      // 合并数据（此处简单以第一个文件为主，后续可扩展为主键合并）
      const mainFile = fileDataMap.value[filesNeeded[0]];
      if (!mainFile || mainFile.length === 0) {
        alert('主文件数据为空，无法生成图表');
        return;
      }
      // 生成ECharts配置
      const newChartOption = generateEChartOption(config, fileDataMap.value);
      if (newChartOption) {
        chartOption.value = null;
        nextTick(() => {
          chartOption.value = newChartOption;
        });
      }
    }

    // 生成ECharts配置，支持多文件数据查找
    function generateEChartOption(config, fileDataMap) {
      const {
        xAxis,
        yAxis,
        series,
        title,
        colorScheme,
        animation
      } = config;
      // 以x轴所在文件为主，按x轴字段遍历
      const mainData = fileDataMap[xAxis.file];
      if (!mainData || mainData.length === 0) {
        alert('主文件数据为空，无法生成图表');
        return null;
      }
      // x轴数据
      const xData = mainData.map(row => row[xAxis.field]);
      // y轴数据（支持多文件多字段）
      let yDataArr = [];
      if (Array.isArray(yAxis)) {
        yDataArr = yAxis.map(y => {
          const yFileData = fileDataMap[y.file];
          // 简单按x轴顺序查找（可扩展为主键合并）
          return mainData.map(row => {
            // 按x轴字段值查找yFileData中对应行
            const match = yFileData.find(r => r[xAxis.field] === row[xAxis.field]);
            return match ? parseFloat(match[y.field]) || 0 : null;
          });
        });
      } else if (yAxis) {
        const yFileData = fileDataMap[yAxis.file];
        yDataArr = [mainData.map(row => {
          const match = yFileData.find(r => r[xAxis.field] === row[xAxis.field]);
          return match ? parseFloat(match[yAxis.field]) || 0 : null;
        })];
      }
      // series配置
      const seriesArr = (Array.isArray(yAxis) ? yAxis : [yAxis]).map((y, idx) => ({
        name: y.field,
        type: selectedChartType.value.toLowerCase(),
        data: yDataArr[idx],
        animationDuration: animation ? 1500 : 0,
        itemStyle: {
          color: getColorByScheme(colorScheme)
        }
      }));
      // 基础option
      const option = {
        title: {
          text: title || `${Array.isArray(yAxis) ? yAxis.map(y => y.field).join(',') : yAxis.field} vs ${xAxis.field}`,
          left: 'center',
          textStyle: {
            fontSize: 16,
            fontWeight: 'bold'
          }
        },
        tooltip: {
          trigger: 'axis'
        },
        legend: {
          data: Array.isArray(yAxis) ? yAxis.map(y => y.field) : [yAxis.field],
          top: 'bottom'
        },
        toolbox: {
          show: true,
          feature: {
            dataView: {
              show: true,
              readOnly: false
            },
            magicType: {
              show: true,
              type: ['line', 'bar']
            },
            restore: {
              show: true
            },
            saveAsImage: {
              show: true
            }
          }
        },
        xAxis: {
          type: 'category',
          data: xData,
          axisLabel: {
            interval: 0,
            rotate: xData.length > 10 ? 45 : 0
          }
        },
        yAxis: {
          type: 'value',
          name: Array.isArray(yAxis) ? yAxis.map(y => y.field).join(',') : yAxis.field
        },
        series: seriesArr,
        animation: animation,
        animationDuration: animation ? 1500 : 0
      };
      // 饼图特殊处理
      if (selectedChartType.value === 'Pie' || selectedChartType.value === 'pie') {
        option.series = [{
          name: Array.isArray(yAxis) ? yAxis.map(y => y.field).join(',') : yAxis.field,
          type: 'pie',
          radius: '60%',
          data: xData.map((name, i) => ({
            name,
            value: yDataArr[0][i]
          })),
          emphasis: {
            itemStyle: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }];
        delete option.xAxis;
        delete option.yAxis;
      }
      return option;
    }

    // 根据配色方案获取颜色
    function getColorByScheme(scheme) {
      const colorSchemes = {
        default: '#5470c6',
        blue: '#1890ff',
        green: '#52c41a',
        warm: '#fa8c16',
        cool: '#13c2c2'
      };
      return colorSchemes[scheme] || colorSchemes.default;
    }
    const __returned__ = {
      showFileUpload,
      UploadFiles,
      showDataPreview,
      currentDataFile,
      previewData,
      workspaceFiles,
      fileDataMap,
      showStructurePanel,
      currentStructureFile,
      selectedChartType,
      chartConfig,
      chartHistory,
      showHistory,
      openHistory,
      previewChart,
      deleteChart,
      onChartTypeSelect,
      handleWorkspaceUpdate,
      handleWorkspaceRemove,
      handleWorkspacePreview,
      loadFilePreview,
      handleWorkspaceClear,
      handleShowStructure,
      handleStructureMinimize,
      handleColumnDrag,
      handleConfigChange,
      handleGenerateChart,
      generateEChartOption,
      getColorByScheme,
      TopBar,
      SideBar,
      ChartTypeSelector,
      ChartDisplay,
      ChartHistoryModal,
      FileUploadModal,
      DataPreviewModal,
      FileWorkspace,
      FileStructurePanel,
      ChartConfigPanel,
      get getFilePreview() {
        return getFilePreview;
      },
      ref,
      nextTick,
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
