// 获取图表配置选项
export function getChartConfigOptions(chartType) {
  const commonOptions = COMMON_CONFIG_OPTIONS;
  const specificOptions = CHART_SPECIFIC_OPTIONS[chartType] || {};
  return {
    ...commonOptions,
    ...specificOptions
  };
}

// 按分类组织配置选项
export function getChartConfigByCategory(chartType) {
  const allOptions = getChartConfigOptions(chartType);
  const categories = {};
  Object.entries(allOptions).forEach(([key, option]) => {
    const category = option.category || 'other';
    if (!categories[category]) {
      categories[category] = {};
    }
    categories[category][key] = option;
  });
  return categories;
}

// 获取配置项的默认值
export function getDefaultConfig(chartType) {
  const options = getChartConfigOptions(chartType);
  const defaultConfig = {};
  Object.entries(options).forEach(([key, option]) => {
    defaultConfig[key] = option.default;
  });
  return defaultConfig;
}

// 验证配置值
export function validateConfigValue(configKey, value, configDef) {
  const errors = [];

  // 类型验证
  switch (configDef.type) {
    case CONFIG_TYPES.NUMBER:
      if (typeof value !== 'number' || isNaN(value)) {
        errors.push(`${configKey} 必须是数字`);
      } else {
        if (configDef.min !== undefined && value < configDef.min) {
          errors.push(`${configKey} 不能小于 ${configDef.min}`);
        }
        if (configDef.max !== undefined && value > configDef.max) {
          errors.push(`${configKey} 不能大于 ${configDef.max}`);
        }
      }
      break;
    case CONFIG_TYPES.SELECT:
      if (configDef.options && !configDef.options.some(opt => opt.value === value)) {
        errors.push(`${configKey} 不是有效选项`);
      }
      break;
    case CONFIG_TYPES.COLOR:
      if (!/^#[0-9A-F]{6}$/i.test(value)) {
        errors.push(`${configKey} 不是有效的颜色值`);
      }
      break;
    case CONFIG_TYPES.JSON:
      try {
        JSON.parse(value);
      } catch (e) {
        errors.push(`${configKey} 不是有效的JSON格式`);
      }
      break;
  }
  return errors;
}

// 检查条件依赖
export function checkCondition(condition, currentConfig) {
  if (!condition) return true;
  return currentConfig[condition.field] === condition.value;
}

