// ChartConfigPanel 相关通用逻辑抽离

/**
 * 校验当前配置是否有效
 * @param {string} type - 图表类型
 * @param {object} config - 当前配置对象
 * @returns {boolean}
 */
export function isConfigValid(type, config) {
  const lowerType = (type || '').toLowerCase();
  if (["pie", "funnel", "gauge"].includes(lowerType)) {
    return config.category && config.category.field && config.value && config.value.field;
  }
  const xValid = config.xAxis && config.xAxis.field;
  const y = config.yAxis;
  const yValid = Array.isArray(y) ? y.length > 0 : (y && y.field);
  return xValid && yValid;
}

/**
 * 获取图表类型对应的icon
 * @param {string} type
 * @param {object} chartIcons
 * @returns {string}
 */
export function getChartIcon(type, chartIcons) {
  return chartIcons[type] || '';
}

/**
 * 延迟显示tooltip
 * @param {Function} cb
 * @param {number} delay
 * @returns {number} timeoutId
 */
export function showTooltipWithDelay(cb, delay = 1200) {
  return setTimeout(cb, delay);
}

/**
 * 取消tooltip延迟
 * @param {number} timeoutId
 */
export function clearTooltipTimeout(timeoutId) {
  if (timeoutId) clearTimeout(timeoutId);
}
