// ChartTypeConfig.js
// 图表类型的动态配置描述，便于模块化和可扩展
/* eslint-disable */
export const chartTypeConfig = {
    Line: {
        mapping: [
            { key: 'xAxis', label: 'X Axis', type: 'dimension', required: true },
            { key: 'yAxis', label: 'Y Axis', type: 'measure', required: true, multiple: true },
        ],
        filter: [
            {
                type: 'and',
                children: [
                    {
                        type: 'filter',
                        dimension: 'xAxis',
                        operator: '!=',
                        value: ''
                    },
                    {
                        type: 'filter',
                        dimension: 'yAxis',
                        operator: '>',
                        value: '0'
                    },
                    {
                        type: 'select',
                        key: 'dataRange',
                        label: 'Data Range',
                        options: ['all', 'first100', 'last100', 'sample'],
                        value: 'all'
                    }
                ]
            }
        ],
        advanced: [
            { key: 'lineWidth', label: 'Line Width', type: 'number', min: 1, max: 10, default: 2 }
        ]
    },
    Bar: {
        mapping: [
            { key: 'xAxis', label: 'X Axis', type: 'dimension', required: true },
            { key: 'yAxis', label: 'Y Axis', type: 'measure', required: true, multiple: true },
        ],
        filter: [
            {
                type: 'or',
                children: [
                    {
                        type: 'filter',
                        dimension: 'yAxis',
                        operator: '>=',
                        value: '10'
                    },
                    {
                        type: 'expression',
                        expr: '[xAxis] == "A" && [yAxis] < 100'
                    },
                    {
                        type: 'select',
                        key: 'dataRange',
                        label: 'Data Range',
                        options: ['all', 'first100', 'last100', 'sample'],
                        value: 'all'
                    }
                ]
            }
        ],
        advanced: [
            { key: 'barWidth', label: 'Bar Width', type: 'number', min: 1, max: 50, default: 10 }
        ]
    },
    Pie: {
        mapping: [
            { key: 'category', label: 'Category', type: 'dimension', required: true },
            { key: 'value', label: 'Value', type: 'measure', required: true },
        ],
        filter: [
            {
                type: 'filter',
                dimension: 'value',
                operator: '>',
                value: '0'
            }
        ],
        advanced: [
            { key: 'donut', label: 'Donut Mode', type: 'checkbox', default: false }
        ]
    }
    // ... 其他图表类型可继续扩展
}
