/* eslint-disable */

// 箱线图生成器
export default function boxplotOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const { seriesList, seriesData_boxplot } = seriesData;

    return {
        title: {
            text: 'Boxplot Chart',
            left: 'center',
            textStyle: {
                fontSize: 16,
                fontWeight: 'bold',
            }
        },
        legend: {
            type: 'scroll',
            show: config.legendVisible !== false,
            top: config.legendPosition || 'bottom',
            data: seriesList.map(item => item.name),
        },
        tooltip: {
            trigger: 'item',
            axisPointer: {
                type: 'shadow'
            }
        },
        xAxis: {
            type: 'category',
            data: xData,
            axisLabel: {
                interval: 0,
                rotate: xData.length > 10 ? 45 : 0
            },
            splitArea: {
                show: true
            },
            splitLine: {
                show: false
            }
        },
        yAxis: {
            type: 'value',
            name: 'Value',
            splitArea: {
                show: true
            }
        },
        series: [

        ]
    }
}
