import FileUploadModal from '../components/FileUploadModal.vue';
import DataPreviewModal from '../components/DataPreviewModal.vue';
import FileWorkspace from '../components/FileWorkspace.vue';
import DataMappingPanel from '../components/DataMappingPanel.vue';
import FileStructurePanel from '../components/FileStructurePanel.vue';
import ThemeButton from '../components/ThemeButton.vue';
import { getFilePreview } from '../services/FileServices.js';
import { useTheme } from '../composables/useTheme.js';
import { useFileStructure } from '../composables/useFileStructure.js';
import { useDataFiltering } from '../composables/useDataFiltering.js';
import { ref, nextTick, onMounted } from 'vue';

// 主题管理

export default {
  __name: 'ChartVisualizations',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    /* eslint-disable */
    const {
      isDarkMode,
      toggleTheme,
      getThemeIcon,
      initTheme
    } = useTheme();

    // 文件结构管理
    const {
      analyzeFileStructure,
      mergeFilesData
    } = useFileStructure();

    // 数据过滤
    const {
      setRawData
    } = useDataFiltering();

    // 组件状态
    const selectedChartType = ref('line');
    const showFileStructure = ref(false);
    const currentMappingData = ref({});
    const isDragging = ref(false);

    // 文件上传相关
    const showFileUpload = ref(false);
    function UploadFiles() {
      showFileUpload.value = true;
    }

    // 数据预览相关
    const showDataPreview = ref(false);
    const currentDataFile = ref(null);
    const previewData = ref([]);

    // 工作区相关
    const workspaceFiles = ref([]);
    function DataPreview() {
      // 这里可以根据当前图表获取对应的数据文件信息
      currentDataFile.value = {
        name: 'currentFile.csv'
      };
      // 这里可以获取当前图表的数据用于预览
      previewData.value = [{
        column1: 'A',
        column2: 100
      }, {
        column1: 'B',
        column2: 200
      }, {
        column1: 'C',
        column2: 300
      }];
      showDataPreview.value = true;
    }

    // 测试图表绘制
    const chartOption = ref({
      title: {
        text: '示例图表'
      },
      tooltip: {},
      xAxis: {
        data: ['A', 'B', 'C', 'D']
      },
      yAxis: {},
      series: [{
        type: 'line',
        data: [23, 45, 12, 37]
      }]
    });

    // 图表存储区（可从后端获取）
    const chartHistory = ref([{
      title: '销售柱状图',
      option: {
        title: {
          text: '销售柱状图'
        },
        tooltip: {},
        xAxis: {
          data: ['一季度', '二季度', '三季度', '四季度']
        },
        yAxis: {},
        series: [{
          type: 'bar',
          data: [120, 200, 150, 80]
        }]
      }
    }, {
      title: '折线趋势',
      option: {
        title: {
          text: '折线趋势'
        },
        tooltip: {},
        xAxis: {
          data: ['1月', '2月', '3月', '4月']
        },
        yAxis: {},
        series: [{
          type: 'line',
          data: [30, 50, 80, 65]
        }]
      }
    }, {
      title: '饼图示例',
      option: {
        title: {
          text: '饼图示例'
        },
        tooltip: {},
        series: [{
          type: 'pie',
          radius: '60%',
          data: [{
            value: 40,
            name: 'A类'
          }, {
            value: 32,
            name: 'B类'
          }, {
            value: 28,
            name: 'C类'
          }]
        }]
      }
    }]);
    const showHistory = ref(false);
    function openHistory() {
      showHistory.value = true;
    }
    function previewChart(item) {
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = JSON.parse(JSON.stringify(item.option));
        showHistory.value = false;
      });
    }
    function deleteChart(idx) {
      chartHistory.value.splice(idx, 1);
    }

    // 图表选择
    function onChartTypeSelect(type) {
      console.log(`Selected chart type: ${type}`);
      selectedChartType.value = type;

      // 根据选择的图表类型更新示例图表
      updateExampleChart(type);
    }

    // 更新示例图表
    function updateExampleChart(type) {
      const exampleData = {
        line: {
          title: {
            text: '折线图示例'
          },
          tooltip: {},
          xAxis: {
            data: ['1月', '2月', '3月', '4月', '5月', '6月']
          },
          yAxis: {},
          series: [{
            type: 'line',
            data: [23, 45, 56, 78, 32, 46],
            smooth: true
          }]
        },
        bar: {
          title: {
            text: '柱状图示例'
          },
          tooltip: {},
          xAxis: {
            data: ['A', 'B', 'C', 'D', 'E']
          },
          yAxis: {},
          series: [{
            type: 'bar',
            data: [120, 200, 150, 80, 70]
          }]
        },
        pie: {
          title: {
            text: '饼图示例'
          },
          tooltip: {
            trigger: 'item'
          },
          series: [{
            type: 'pie',
            radius: '60%',
            data: [{
              value: 40,
              name: 'A类'
            }, {
              value: 32,
              name: 'B类'
            }, {
              value: 28,
              name: 'C类'
            }, {
              value: 20,
              name: 'D类'
            }]
          }]
        },
        scatter: {
          title: {
            text: '散点图示例'
          },
          tooltip: {},
          xAxis: {
            scale: true
          },
          yAxis: {
            scale: true
          },
          series: [{
            type: 'scatter',
            data: [[10, 20], [15, 25], [20, 30], [25, 35], [30, 40]]
          }]
        },
        radar: {
          title: {
            text: '雷达图示例'
          },
          tooltip: {},
          radar: {
            indicator: [{
              name: '销售',
              max: 100
            }, {
              name: '管理',
              max: 100
            }, {
              name: '技术',
              max: 100
            }, {
              name: '客服',
              max: 100
            }, {
              name: '研发',
              max: 100
            }]
          },
          series: [{
            type: 'radar',
            data: [{
              value: [80, 90, 70, 85, 75],
              name: '预算分配'
            }]
          }]
        }
      };
      chartOption.value = exampleData[type] || exampleData.line;
    }

    // 文件结构面板
    function toggleFileStructure() {
      showFileStructure.value = !showFileStructure.value;
    }

    // 工作区相关方法
    async function handleWorkspaceUpdate(files) {
      workspaceFiles.value = [...files];
      console.log('Workspace updated:', workspaceFiles.value.length, 'files');

      // 自动分析文件结构
      for (const file of workspaceFiles.value) {
        if (!file.columns) {
          try {
            await analyzeFileStructure(file);
          } catch (error) {
            console.error('Failed to analyze file:', file.name, error);
          }
        }
      }

      // 更新合并数据
      updateMergedData();
    }
    function handleWorkspaceRemove(file) {
      console.log('File removed from workspace:', file.name);
      // 从工作区数组中移除文件
      const index = workspaceFiles.value.findIndex(f => f.id === file.id);
      if (index !== -1) {
        workspaceFiles.value.splice(index, 1);
      }
      updateMergedData();
    }
    function handleWorkspacePreview(file) {
      // 预览工作区中的文件
      console.log('Preview workspace file:', file.name);
      currentDataFile.value = file;

      // 使用与 FileUploadModal 相同的预览逻辑
      if (file.previewData) {
        // 如果文件已经有预览数据，直接使用
        previewData.value = file.previewData;
        showDataPreview.value = true;
      } else {
        // 否则读取文件内容
        loadFilePreview(file);
      }
    }

    // 加载文件预览数据
    async function loadFilePreview(file) {
      try {
        const previewResult = await getFilePreview(file.id);
        previewData.value = previewResult.data;
        showDataPreview.value = true;
      } catch (error) {
        console.error('Preview failed:', error);
        alert('Preview failed: ' + error.message);
      }
    }
    function handleWorkspaceClear() {
      console.log('Workspace cleared');
      workspaceFiles.value = [];
      updateMergedData();
    }

    // 处理显示文件结构
    function handleShowStructure(files) {
      showFileStructure.value = true;
    }

    // 文件预览处理
    function handleFilePreview(file) {
      handleWorkspacePreview(file);
    }

    // 拖拽处理
    function handleColumnDragStart(columnData) {
      isDragging.value = true;
      console.log('Column drag started:', columnData);
    }
    function handleColumnDragEnd() {
      isDragging.value = false;
      console.log('Column drag ended');
    }

    // 图表更新处理
    function handleChartUpdate(option) {
      chartOption.value = null;
      nextTick(() => {
        chartOption.value = option;
      });
    }

    // 映射变化处理
    function handleMappingChanged(mappingInfo) {
      currentMappingData.value = mappingInfo;
      console.log('Mapping changed:', mappingInfo);
    }

    // 更新合并数据
    function updateMergedData() {
      if (workspaceFiles.value.length === 0) {
        setRawData([]);
        return;
      }
      try {
        const mergedData = mergeFilesData(workspaceFiles.value, currentMappingData.value.mappedData || {});
        setRawData(mergedData);
      } catch (error) {
        console.error('Failed to merge data:', error);
        setRawData([]);
      }
    }

    // 初始化
    onMounted(() => {
      initTheme();
    });
    const __returned__ = {
      isDarkMode,
      toggleTheme,
      getThemeIcon,
      initTheme,
      analyzeFileStructure,
      mergeFilesData,
      setRawData,
      selectedChartType,
      showFileStructure,
      currentMappingData,
      isDragging,
      showFileUpload,
      UploadFiles,
      showDataPreview,
      currentDataFile,
      previewData,
      workspaceFiles,
      DataPreview,
      chartOption,
      chartHistory,
      showHistory,
      openHistory,
      previewChart,
      deleteChart,
      onChartTypeSelect,
      updateExampleChart,
      toggleFileStructure,
      handleWorkspaceUpdate,
      handleWorkspaceRemove,
      handleWorkspacePreview,
      loadFilePreview,
      handleWorkspaceClear,
      handleShowStructure,
      handleFilePreview,
      handleColumnDragStart,
      handleColumnDragEnd,
      handleChartUpdate,
      handleMappingChanged,
      updateMergedData,
      TopBar,
      SideBar,
      ChartTypeSelector,
      ChartDisplay,
      ChartHistoryModal,
      FileUploadModal,
      DataPreviewModal,
      FileWorkspace,
      DataMappingPanel,
      FileStructurePanel,
      ThemeButton,
      get getFilePreview() {
        return getFilePreview;
      },
      get useTheme() {
        return useTheme;
      },
      get useFileStructure() {
        return useFileStructure;
      },
      get useDataFiltering() {
        return useDataFiltering;
      },
      ref,
      nextTick,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
