/* eslint-disable */

// 饼图 option 生成器
export default function pieOption(config, fileDataMap, xData, yDataArr, selectedChartType, seriesData, customOption = {}) {
    const { title, animation } = config;
    // 自动合并同名项
    let pieSeries;
    if (Array.isArray(seriesData) && seriesData.length > 0) {
        pieSeries = seriesData;
    } else {
        const pieMap = new Map();
        xData.forEach((name, i) => {
            const value = yDataArr[0][i];
            if (pieMap.has(name)) {
                pieMap.set(name, pieMap.get(name) + value);
            } else {
                pieMap.set(name, value);
            }
        });
        pieSeries = Array.from(pieMap.entries()).map(([name, value]) => ({ name, value }));
    }
    console.log('[pieOption] pieSeries:', pieSeries);
    return {
        title: {
            text: title || '',
            left: 'center',
            textStyle: { fontSize: 16, fontWeight: 'bold' }
        },
        tooltip: { trigger: 'item' },
        legend: {
            type: 'scroll',
            show: config.legendVisible !== false,
            top: config.legendPosition || 'bottom',
        },
        series: [
            {
                name: title || '',
                type: 'pie',
                radius: '60%',
                data: pieSeries,
                emphasis: {
                    itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                },
                animation: animation,
                animationDuration: animation ? 1500 : 0
            }
        ],
        ...customOption
    };
}
