            return fileData
        } catch (error) {
            console.error('缓存文件失败:', error)
            throw error
        }
    }

    // 读取文件内容预览
    async readFileContent(file) {
        return new Promise((resolve, reject) => {
            const reader = new FileReader()
            reader.onload = (e) => {
                try {
                    const content = e.target.result
                    if (file.name.endsWith('.csv')) {
                        resolve(this.parseCSVPreview(content))
                    } else if (file.name.match(/\.(xlsx|xls)$/)) {
                        resolve({ type: 'excel', preview: 'Excel file cached' })
                    } else {
                        resolve({ type: 'unknown', preview: 'File cached' })
                    }
                } catch (error) {
                    reject(error)
                }
            }
            reader.onerror = reject
            reader.readAsText(file)
        })
    }

    // 解析CSV预览数据
    parseCSVPreview(content) {
        const lines = content.split('\n').filter(line => line.trim())
        if (lines.length === 0) {
            return { type: 'csv', headers: [], preview: [], totalRows: 0 }
        }

        // 简单的CSV解析（处理基本的逗号分隔）
        const parseCSVLine = (line) => {
            const result = []
            let current = ''
            let inQuotes = false

            for (let i = 0; i < line.length; i++) {
                const char = line[i]

                if (char === '"') {
                    inQuotes = !inQuotes
                } else if (char === ',' && !inQuotes) {
                    result.push(current.trim())
                    current = ''
                } else {
                    current += char
                }
            }

            result.push(current.trim())
            return result
        }

        const headers = parseCSVLine(lines[0])
        // 返回最多200行数据用于预览
        const preview = lines.slice(1, 201).map(line => {
            const values = parseCSVLine(line)
            const row = {}
            headers.forEach((header, index) => {
                row[header] = values[index] || ''
            })
            return row
        })

        return { type: 'csv', headers, preview, totalRows: lines.length - 1 }
    }

